import React, { Component } from 'react';
import request from 'superagent';
import Moment from 'moment';
import '../../assets/css/videos/videos.css';
import { stringify } from 'querystring';

class LatestNews extends Component {
    state = {
        error: null,
        isLoaded: false,
        isLoading: false,
        hasMore: true,
        selectedType: 'Latest_News',
        seriesData: [],
        baseUrl: 'https://back.cricwick.net/api',
        page: 1,
        perPage: 5,
        showFull:false,

    }

    componentDidMount = async () => {
        await this.loadSeriesRecords();
    }

    loadSeriesRecords = async () => {
        const { page, perPage, baseUrl } = this.state;

        // 1) All Videos :
        //    Base URL =  https://back.cricwick.net/api/news?page=1&page_size=6
        //    Composed =  /news?page=1&page_size=5


        let Url = baseUrl
            + '/'
            + 'news'
            + '?'
            + 'page=' + page
            + '&'
            + 'page_size='
            + perPage

        console.log(Url);

        await request
            .get(Url)
            .set('Accept', 'application/json')
            .then(res => {
                this.setState({
                    isLoaded: true,
                    isLoading: false,
                    seriesData: res.body,
                    hasMore: (res.body.length < perPage)
                });

            })
            .catch(error => {
                this.setState({
                    isLoaded: false,
                    error
                });
            });
    }

   handleClikc =()=>{
       this.setState({
             showFull:!this.state.showFull
       });        
   } 

   
    render() {
        const { isLoaded, error, seriesData, showFull } = this.state;

        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {
            return (
                <div>
                    <div hidden={showFull}>
                         Showing All Detaill
                    </div>
                     {seriesData.data.map((news, i) => (
                        <div className="flex-d" key={news.id} onClick={()=> this.handleClikc()} >
                            <div className="card mb-1 p-2">
                                <div className='card-body p-0'>
                                    <div>
                                        <img className='card-videos-small'
                                            src={news.image}
                                            alt="no Iamge" />
                                    </div>

                                    <div>
                                        <div className='card-videos-small-date'>
                                            <span style={{float:"left"}}> {news.series_name} </span>
                                            <span style={{float:"right"}}> {Moment(new Date(news.created_at)).format("DD MMM,YYYY")} </span>
                                        </div>

                                        <div className='card-video-title-text' >{news.title}</div>
                                        <div className='card-video-title-text'> {news.body.substr(4,100)} </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            );
        }
    }
}

export default LatestNews;
