import React from "react";
import { Link } from "react-router-dom";
import "../../assets/css/common/news.css";

const News = props => {
  const { match } = props;

  if (match) {
    return (
      <ul className="news-list">
        {match.map((news, i) => (
          <li key={news.id}>
            <Link
              to={{
                pathname: `/stories/news/${news.id}`,
                state: {
                  objSelectedStory: news,
                  selectedType: "Latest_News",
                  showFull: true
                }
              }}
            >
              <h6> {news.title} </h6>
            </Link>
          </li>
        ))}
      </ul>
    );
  }
};

export default News;
