import React, { Component } from 'react';
import '../../assets/css/home/homeCenterPanel.css';
import Match from './match';
//import axios from 'axios';

class HomeCenterPanel extends Component {

    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            homeContents : [],
            tempContents : [],
            series : [],
        };
    }

    componentDidMount() {
         
        // fetch("http://back.cricwick.net/api/v3/view_lists/get_by_name?view=home")
        //Home Series Api  
        fetch("http://back.cricwick.net/api/v3/view_lists/get_by_name?view=home&web_user=1&page=1")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        homeContents: result
                    });
                },

                // Handling Erro
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
           )
    }


   render() {
        const { error, isLoaded, homeContents} = this.state;
        
        let tempContents1 =  homeContents.filter(x => x.type !== 'generic-home');
        
        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {
          
            // tempContents1.map((series, i) => (
            //     series.data.map((matches, i) => (
            //         matches.data.map((match, i) => (
            //              console.log(match);     
            //         ))
            //     ))
            // ));

        return(
          <div>
                {tempContents1.map((series, i) => (
                    <div key={i}>
                        {series.title}
                        <div>
                            {series.data.map((matches, i) => (
                              <div key={i}>
                                    {matches.title}
                                    {matches.data.map((match,i)=>(
                                        <div key={i}>
                                            {match.title}
                                            <Match match={match}
                                                    type={matches.type} />
                                        </div>
                                    ))}   
                              </div>   
                            ))}
                        </div>
                    </div>     
              ))}
          </div> 
        )                 
      }
    }
}

export default HomeCenterPanel;
