import React, { Component } from 'react';
import request from 'superagent';
import '../../assets/css/videos/videos.css';
import CardImage from '../common/cardImage';

class AllVideos extends Component {
    constructor(props) {
        super(props)
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            seriesData: [],
            selectedType: 'Featured',
            baseUrl: 'https://back.cricwick.net/api/v1',
            page: 1,
            perPage: 5,
            telco: 'telco=mobilink'
        };

    }

    componentDidMount = async () => {
        await this.loadSeriesRecords();
    }

    loadSeriesRecords = async () => {
        const { page, perPage, baseUrl, telco } = this.state;

        let Url = '';
        // 1) Featured Videos :
        //    Base URL =  https://back.cricwick.net/api/v1
        //    Composed =  /video/get_by_video_type/in_house_content/1/5?telco=mobilink

        
        Url = baseUrl + "/video"
            + "/"
            + "get_by_video_type"
            + "/"
            + "in_house_content"
            + "/"
            + page
            + "/"
            + perPage
            + "?" 
            + telco

        await request
            .get(Url)
            .set('Accept', 'application/json')
            .then(res => {
                this.setState({
                    isLoaded: true,
                    isLoading: false,
                    seriesData: res.body,
                    hasMore: (res.length < perPage)
                })

            })
            .catch(error => {
                this.setState({
                    isLoaded: false,
                    error
                });
            });
    }

    render() {
        const { error, isLoaded, seriesData } = this.state;
        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {
            //------------------------------------------------------------------- 
            console.log(seriesData);
            return (
                <div>
                    {seriesData.videos.map((video, i) => (
                        <div className="flex-d">
                            <div className="card mb-1 p-0">
                                <div className='card-body p-3'>
                                    <div>
                                        <img className='home-center-series-videos-small'
                                            src={video.thumb}
                                            alt="no Iamge" />
                                    </div>

                                    <div className='home-center-series-videos-small-text' >
                                        <h6>{video.title}</h6>
                                        <h6 style={{ fontWeight: "normal" }} >{video.match_desc}</h6>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            )
        }
    }
}

export default AllVideos;
