        //       console.log(i)
        // ));
          console.log(this.state.data);

        return (
           <React.Fragment> 
            <div>
                Test
                 {/* {this.state.data['live_series'].map((item, i) => (
                    <div> {i} </div>
                ))}
                 */}
            </div>
            </React.Fragment>
        );

    }
}

export default Series;
