import React, { Component } from 'react';
import request              from  'superagent';
import axios                from 'axios';

class Series extends Component {
    constructor(props) {
        super()
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            seriesData: [],
            data:[],
            baseUrl: 'https://back.cricwick.net/api/v2/series',
            page: 1,
            perPage: 10,
        }

    }

    componentDidMount() {
        this.loadSeriesRecords();
    }

    loadSeriesRecords = () => {
        const { page, perPage } = this.state;

        const Url = this.state.baseUrl + '/'
            + '?page=' + page
            + '&per_page=' + perPage;

        console.log("Series : ", Url);
       
        try {
            axios.get(Url,{responseType: 'json'}).then(response => {
                this.setState({ data : response.data });
                this.state.data['live_series'].map((item, i) => (
                    console.log("From Loop : ", item)
                ));

            });
            
        }
        catch (err) {
            console.log("Error", err);
            window.location.href = '/404notfound';
        };

        request
            .get(Url)
            .set('Accept','application/json')
            .then(res => {
                this.setState({
                    isLoaded: true,
                    isLoading: false,
                    seriesData: res.body,
                    hasMore: (res.length < perPage)
                });
                console.log( "From Super Agent : ",this.state.seriesData);
                this.state.seriesData['live_series'].map((item, i) => (
                    console.log("From Super Agent : ", item)
                ));
                
            })
            .catch(error => {
                this.setState({
                    isLoaded: false,
                    error
                });
            });
    }

    render() {
       
        return (
            <div>
                <h6>test</h6>
            </div>
        );
    }
}

export default Series;
