import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import '../../assets/css/home/homeLeftPanel.css';

class HomeLeftDownPanel extends Component {

    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            updates: []
        }

    }


    componentDidMount() {
        fetch("http://back.cricwick.net/api/news")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        updates: result
                    });
                },

                // Handling Erro
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }

    render() {
        const { error, isLoaded, updates } = this.state;

        if (!isLoaded) {
            return (<div> Loading Data ....... Please wait......  </div>)
        } else if (error) {
            return (<div> error : {error.message} </div>)
        } else {
            return (
                <div>
                    <div className="card card-block">
                        <h6 className="card-header p-2">Latest Updates</h6>
                        <div className='card-body m-0 p-0'>
                            <ul style={{ marginBottom: 4 + "px", paddingLeft: 20, paddingRight: 8 }}>
                                {updates.data.map(update => (
                                    <li key={update.id}> 
                                        <Link to ={{pathname:'/stories',
                                                    state:{
                                                        news : update,
                                                    } 
                                               }} > 
                                             {update.id} + {' - '}    
                                             {update.title} 
                                        </Link> 
                                    </li>
                                ))}
                            </ul>
                        </div>
                    </div>
                </div>
            );
        }
    }
}

export default HomeLeftDownPanel;
