import React from 'react';
import Moment from 'moment';
import '../../assets/css/videos/videos.css';

const DetailedNews = (props) => {
    const { news } = props;
    console.log("From Detailed News : ", news);
    return (
        <div className="card mb-1 p-2">
            <div className='card-body p-0'>
                <div>
                    <img className='card-news-detail-image-large'
                        src={news.image}
                        alt="no Iamge" />
                </div>

                <div className='card-videos-small-date'>
                    <h6> {news.by}          </h6>
                    <h6> {news.series_name} </h6>
                    <h6> {Moment(new Date(news.created_at ? news.created_at : news.published)).format("DD MMM,YYYY")} </h6>
                </div>
                <div className='card-video-title-text' > {news.title}</div> <br />
                <span dangerouslySetInnerHTML={{ __html: news.body }} />
            </div>
        </div>
    );
}

export default DetailedNews;
