import React, { Component }       from 'react';
import request                    from  'superagent';

class Series extends Component {
    constructor(props) {
        super()
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            seriesData: [],
            baseUrl: 'https://back.cricwick.net/api/v2/series',
            page: 1,
            perPage: 10,
        }

    }

    componentDidMount() {
       this.loadSeriesRecords();    
    }

    loadSeriesRecords = () => {
        const { seriesData, page, perPage } = this.state;

        const Url = this.state.baseUrl + '/'
            + '?page=' + page
            + '&per_page=' + perPage

        console.log(Url);
        //if (hasMore) {
            request
            .get(Url)
            .set('Accept','application/json')
            .then(res => {
                this.setState({
                    isLoaded: true,
                    isLoading: false,
                    seriesData: [...seriesData, ...res.body],
                    hasMore: (res.length < perPage)
                });
            })
            .catch(error => {
                this.setState({
                    isLoaded: false,
                    error
                });
           });
        //}   
    } 


    render() {
        const {seriesData} =  this.state;
        console.log(seriesData);

        return (
            <div>Series Navigation</div>
        );
    }
}

export default Series;
