import React, { Component } from 'react';
import request              from 'superagent';
import Loading              from '../common/loading';
import Match                from  'mathjs';
import Moment               from 'moment';
import {NavLink}               from 'react-router-dom';
import '../../assets/css/series/series.css';
//import axios                from 'axios';

class Series extends Component {
    constructor(props) {
        super(props)
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            seriesData: [],
            selectedType:'live_series',
            urlPath : 'live', 
            baseUrl: 'https://back.cricwick.net/api/v2/series',
            page: 1,
            telco:'mobilink',
            perPage: 10,
        };
    }

    componentDidMount() {
        this.loadSeriesRecords();
        // this.state.seriesData['live_series'].map((item,i)=>(
        //       console.log(item)
        // ));
        //console.log( "From Component Did Mount : " , this.state.seriesData);
    }
   
    loadSeriesRecords = async () =>{
        const {page, perPage, baseUrl} =  this.state;
          // fetch("http://back.cricwick.net/api/v3/view_lists/get_by_name?view=home")
          //Home Series Api  
      
          const Url = baseUrl + '/'
                      + '?page=' + page
                      + '&per_page=' + perPage;

          //console.log(Url);
          //console.log(hasMore);
          
          request
              .get(Url)
              .set('Accept','application/json')
              .then(res => {
                  this.setState(() => {
                      return {
                          isLoaded: true,
                          isLoading: false,
                          seriesData: res.body,
                          hasMore: (res.length < perPage)
                      }
                  });
              })
              .catch(error => {
                  this.setState(()=>{
                      return {
                          isLoaded: false,
                          error
                      }
                  });
             });
      }


      handleScroll = async() => {
        
        let offset = (document.documentElement.scrollTop + window.innerHeight);
        let height = document.documentElement.offsetHeight;

       //console.log("offset : ", Math.ceil(offset));
       //console.log("height : ", height);


        if (Match.ceil(offset) === height) {
            await this.setState((prevState)=>{ return {page: prevState.page + 1 }});
            this.loadSeriesRecord();
        }

    }  

    handleClick = (selectedType, urlPath) => {
        
        if (!selectedType) {
            this.setState(()=>{ return {selectedType: 'live_series', urlPath: urlPath } });
        } else {
            this.setState(()=>{ return {selectedType: 'live_series', urlPath: urlPath } });
        }
        
    }
    
    renderSeries = (data) => {
        const {page, telco, urlPath} =  this.state;
        console.log(`${urlPath}`);


        return (
            <div>
                {data.map((series, i) => (
                    <div key={series.id}>
                        <NavLink to={{
                            pathname: `/series/allseries/${urlPath}`,
                            state: {
                                viewable_type: 'series',
                                viewable_id: series.id,
                                page: page,
                                selectedType:'summary',
                                telco: telco,
                                seriesTitle: series.title
                            }
                        }}>
                            <span className='video-series-title'> {series.title} </span>
                            <br />
                            <span className='video-series-subtitle'>
                                {Moment(new Date(series.start_date)).format("DD MMM")} {" - "}
                                {Moment(new Date(series.start_date)).format("DD MMM")}
                            </span>
                        </NavLink>

                        {data.length !== (i + 1) ? <hr /> : ''}

                    </div>
                ))}
            </div>
        )
    }


    render() {
        const { error, isLoaded, seriesData, selectedType} = this.state;

        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<Loading/>);
        } else {

        return (
            <div>
                <div className='card card-block mb-1 p-3'>
                    <span  className='short-menu-buttons'>
                        <button className='btn btn-default' onClick={() => this.handleClick('live_series'     , 'live')} >Live</button>
                        <button className='btn btn-default' onClick={() => this.handleClick('upcoming_series' ,'upcoming')}>Upcomming</button>
                        <button className='btn btn-default' onClick={() => this.handleClick('recent_series'   , 'recent')}>Result</button>
                    </span>
                </div>
                  
                <div className="card card-block p-3 mb-1">
                    {this.renderSeries(seriesData[selectedType])}        
                </div>  
            </div>
        );
    }
  } 
}

export default Series;
