import React, { Component } from "react";
import config from "../../config.json";
//import http from '../../services/httpService'
import http from "superagent";
import { Link } from 'react-router-dom';
import Articles from '../common/articles';
import LiveMatch from '../common/liveMatch';
import News from '../common/news';
import Tweets from '../common/tweets';
import Videos from '../common/videos';
import '../../assets/css/home/homeCenterPanel.css';

class SeriesSummary extends Component {

  _isMounted = false;

  constructor(props) {
    super(props);
    this.state = {
      seriesData: [],
      isLoading: true,
      isLoaded: false,
      hasMore: true,
      error: null,
      viewable_type: this.props.viewable_type,
      viewable_id: this.props.viewable_id,
      page: 160,
    };
  }

  //  RECEVING PARAMETERS:-  
  //  viewable_type : 'series',
  //  viewable_id   : series.id,
  //  page          : page,
  //  telco         : telco

  //   let Url = 'https://back.cricwick.net/api/v2/view_lists/get_list_items_from_viewable'
  //   Url = Url + `?viewable_type=${series}`
  //             + `&viewable_id=${seriesId}`
  //             + `&page=${page}`
  //             + `&telco=${telco}`



  componentWillUnmount() {
    this._isMounted = false;
    window.removeEventListener('scroll', this.handleScroll);
  }

  componentDidMount = async () => {
    //     this._isMounted = true;
    //     const { viewable_type, viewable_id, page } = this.props;
    //     this.setState({
    //         viewable_id: viewable_id,
    //         viewable_type: viewable_type,
    //         page: page
    //     });

    window.addEventListener('scroll', this.handleScroll);
    //     await this.getSeriesRecords();
  };




  componentWillReceiveProps = async (nextProps) => {

    console.log('component Will Recevie Props', nextProps.viewable_id, nextProps.viewable_type, nextProps.page);

    await this.setState({
      viewable_id: nextProps.viewable_id,
      viewable_type: nextProps.viewable_type,
    });

    await this.getSeriesRecords();
  }


  getSeriesRecords = async () => {
    // const { viewable_type, viewable_id, page } = this.props
    const { viewable_type, viewable_id, page, seriesData } = this.state

    const Url = config.apiEndPoint
      + '/v2/view_lists/get_list_items_from_viewable?viewable_type='
      + viewable_type
      + '&viewable_id=' + viewable_id
      + '&page=' + page
      + '&telco=' + config.telco

    console.log(Url);

    await http
      .get(Url)
      .set('Accept', 'application/json')
      .then(res => {
        this.setState({
          isLoading: false,
          isLoaded: true,
          seriesData: [...seriesData, ...res.body],
          hasMore: (res.body.length > 0 )  
        });

        console.log("response Body : ",res.body);

      }).catch(error => {
        this.setState({
          isLoaded: false,
          error: error
        });

      })

  }


  handleScroll = async () => {
    const { hasMore, page } = this.state;
    const offset = (document.documentElement.scrollTop + window.innerHeight);
    const height = document.documentElement.offsetHeight;

    if (!hasMore) {
      //console.log("offset : ", Math.ceil(offset));
      console.log("offset : ", Math.floor(offset));
      console.log("height : ", height);

      if (Math.floor(offset) === height) {
        console.log("Inside Scroll");
        console.log("HasMore : ", hasMore, " Page:", page);
        await this.setState({ page: this.state.page + 1 });
        await this.loadSeriesRecords();
      }
    } else {
      console.log('End of list....');
    }
  }



  //  https://back.cricwick.net/api/v2/view_lists/
  //  get_list_items_from_viewable?viewable_type=series&
  //  viewable_id=156&page=1&telco=mobilink

  render() {
    const { seriesData, error, isLoading } = this.state;

    if (error) {
      return <div> Error : {error.message} </div>;
    } else if (isLoading) {
      return <div> Loading Data... </div>;
    } else {
      return (
        <div>
          {seriesData.map((matches, i) => (
            <div key={i}>
              {/* <div className="card pt-2 pl-3 mb-1">
                        </div> */}

              {matches.data.map((match, i) => (
                <div key={i}>

                  {match.type === "match_object" && (
                    <div className="card p-3 mb-1">
                      {match.data.match_state !==
                        "Scheduled" ? (
                          <Link to={{
                            pathname: "/summary/",
                            state: {
                              match: match.data
                            }
                          }}>
                            <h6 className="card-title">{matches.title}</h6>
                            <LiveMatch match={match.data} />
                          </Link>
                        )
                        : (
                          <div>
                            <h6 className="card-title"> {matches.title} </h6>
                            <LiveMatch match={match.data} />
                          </div>
                        )}
                    </div>
                  )}

                  {match.type === "videos" && (
                    <div>
                      <Videos
                        match={match.data}
                        firstTitle={true}
                      />
                    </div>
                  )}

                  {match.type === "news" &&
                    match.data.length !== 0 && (
                      <div className="card p-3 mb-1">
                        <News
                          match={
                            match.data.length !== 0
                              ? match.data
                              : null
                          }
                        />
                      </div>
                    )}

                  {match.type === "articles" && (
                    <div>
                      <Articles match={match.data} />
                    </div>
                  )}

                  {match.type === "tweets" && (
                    <div>
                      <Tweets match={match.data} />
                    </div>
                  )}
                </div>
              ))}
            </div>
          ))}
        </div>
      );
    }
  }
}

export default SeriesSummary;
