import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import '../../assets/css/home/homeLeftPanel.css';

class HomeLeftPanel extends Component {

    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            featuredSeries: []
        }

    }


    componentDidMount() {
        fetch("http://back.cricwick.net/api/v2/series/featured")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        featuredSeries: result
                    });
                },

                // Handling Erro
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }

    render() {
        const { error, isLoaded, featuredSeries } = this.state;
        const series = 'series';
        const seriesId='156';
        const page=1;
        const telco='mobilink';

        let Url = 'https://back.cricwick.net/api/v2/view_lists/get_list_items_from_viewable'
            Url = Url + `?viewable_type=${series}`
                      + `&viewable_id=${seriesId}`
                      + `&page=${page}`
                      + `&telco=${telco}`

        if (!isLoaded) {
            return (<div> Loading Data ....... Please wait......  </div>)
        } else if (error) {
            return (<div> error : {error.message} </div>)
        } else {
            return (
                <div>
                    <div className="card card-block">
                        <h6 className="card-header p-2">Featured Series</h6>
                        <div className='card-body m-0 pl-2'>
                         <nav>
                         <ul>
                            {featuredSeries.featured_series.map(series => (
                               
                                <li> 
                                     <Link to={Url}>
                                            <span key={series.id}>
                                                {series.title} <hr />
                                            </span>
                                     </Link>
                                </li>

                            ))}
                            </ul>
                            </nav>      
                        </div>
                    </div>
                </div>
            );
        }
    }
}

export default HomeLeftPanel
