import React, { Component } from 'react';
import LatestNews from './latestNews';
import Articles from './articles';

class Stories extends Component {

    constructor(props) {
        super(props);

        this.state = {
            selectedType: 'Latest_News',
            type:'',
            objMatch:'',
            showFull:false,

        }
    }

    componentDidMount = async ()=> {

        //Getting Props form Parent Component
        const { selectedType} = this.props.location.state;
      
        this.setState(state =>({
                  selectedType: selectedType   
        }));
    }

    handleClick = (selectedType) => {
        this.setState({ selectedType: selectedType });
    }

    render() {

        const { selectedType } = this.state;
        const { obj_match , type, showFull } = this.props.location.state;
        
          return (
            <div>
                <div className='card'>
                    <span>
                        <button className='btn btn-link' onClick={() => this.handleClick('Latest_News')}>Latest News</button>
                        <button className='btn btn-link' onClick={() => this.handleClick('Articles')}>Articles</button>
                    </span>
                </div>

                {selectedType === 'Latest_News' &&
                    <div>
                        <LatestNews obj_match={obj_match} type={type} showFull={showFull} />
                    </div>
                }

                {selectedType === 'Articles' &&
                    <div>
                        <Articles obj_match={obj_match} type={type} showFull={showFull} />
                    </div>
                }

            </div>
        );

    }
}

export default Stories;
