import React, { Component } from "react";
import config from "../../config.json";
//import http from '../../services/httpService'
import http from "superagent";
import { Link } from 'react-router-dom';
import Articles from '../common/articles';
import LiveMatch from '../common/liveMatch';
import News from '../common/news';
import Tweets from '../common/tweets';
import Videos from '../common/videos';
import '../../assets/css/home/homeCenterPanel.css';

class SeriesSummary extends Component {

    _isMounted = false;

    constructor(props) {
        super(props);
        this.state = {
            seriesData: [],
            isLoading: true,
            isLoaded: false,
            hasMore: true,
            error: null,
            viewable_type: null,
            viewable_id: null,
            page: 1,
        };
    }

    //  RECEVING PARAMETERS:-  
    //  viewable_type : 'series',
    //  viewable_id   : series.id,
    //  page          : page,
    //  telco         : telco

    //   let Url = 'https://back.cricwick.net/api/v2/view_lists/get_list_items_from_viewable'
    //   Url = Url + `?viewable_type=${series}`
    //             + `&viewable_id=${seriesId}`
    //             + `&page=${page}`
    //             + `&telco=${telco}`

    componentWillUnmount() {
        this._isMounted = false;
    }

    componentWillReceiveProps(nextProps){
        const { viewable_type, viewable_id, page } = this.props;
       
        console.log('component Will Recevie Props', viewable_id, viewable_type, page);        
               this.setState({
                 viewable_id : viewable_id,
               viewable_type : viewable_type,
                        page : page 
        });
    }

    componentDidMount = async () => {
        this._isMounted = true;
        const { viewable_type, viewable_id, page } = this.props;
        this.setState({
            viewable_id: viewable_id,
            viewable_type: viewable_type,
            page: page
        });

        console.log("Component Did Mount Called...")
        await this.getSeriesRecords();
    };

    getSeriesRecords = async () => {
        // const { viewable_type, viewable_id, page } = this.props
        const { viewable_type, viewable_id, page } = this.state

        console.log('From Get Series Records : ', viewable_id, viewable_type, page); 

        
        const Url = config.apiEndPoint
            + '/v2/view_lists/get_list_items_from_viewable?viewable_type='
            + viewable_type
            + '&viewable_id=' + viewable_id
            + '&page=' + page
            + '&telco=' + config.telco

            console.log(Url);

       await http
            .get(Url)
            .set('Accept', 'application/json')
            .then(res => {
                if (this._isMounted) {
                    this.setState({
                        isLoading: false,
                        isLoaded: true,
                        seriesData: res.body,
                    });
                }

            }).catch(error => {
                this.setState({
                    isLoaded: false,
                    error: error
                });

            })

        }
        
        //  https://back.cricwick.net/api/v2/view_lists/
        //  get_list_items_from_viewable?viewable_type=series&
        //  viewable_id=156&page=1&telco=mobilink
        
        render() {
            const { seriesData, error, isLoading } = this.state;
            
            if (error) {
                return <div> Error : {error.message} </div>;
            } else if (isLoading) {
                return <div> Loading Data... </div>;
            } else {
                console.log("Series Summary From Render :", this.state.seriesData)
                return (
                <div>
                    {seriesData.map((matches, i) => <div key={i}>

                        <div className="card pt-2 pl-3 mb-1">
                            <h6 className="card-title">
                                {matches.title}
                            </h6>
                        </div>

                        {matches.data.map((match, i) => (
                            <div key={i}>
                                {match.type === "match_object" && (
                                    <div className="card p-3 mb-1">
                                        {match.data.match_state !==
                                            "Scheduled" ?
                                            (<Link to={{ pathname: "/summary/", state: { match: match.data } }}>
                                                <LiveMatch match={match.data} />
                                            </Link>)
                                            :
                                            (<LiveMatch match={match.data} />)}
                                    </div>
                                )}

                                {match.type === "videos" && (
                                    <div>
                                        <Videos match={match.data} firstTitle={true} />
                                    </div>
                                )}

                                {(match.type === "news" && match.data.length !== 0) && (
                                    <div className="card p-3 mb-1">
                                        <News match={match.data.length !== 0 ? match.data : null} />
                                    </div>
                                )}

                                {match.type === "articles" && (
                                    <div>
                                        <Articles match={match.data} />
                                    </div>
                                )}

                                {match.type === "tweets" && (
                                    <div>
                                        <Tweets match={match.data} />
                                    </div>
                                )}
                            </div>
                        ))}
                    </div>)}
                </div>
            );
        }
    }
}

export default SeriesSummary;
