import React, { Component } from "react";
import Moment from "moment";
import "../../assets/css/home/genericHome.css";

class OtherContentsIdentifire extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  render() {
    const { contents, typeVideo } = this.props;
    let subType = contents.sub_type;

    if (!contents.data) return <div> {contents.title} </div>;

    if (typeVideo === "video") {
        console.log(contents)  
        return (
          <div className="flex-d">
                    <div className="card mb-1 p-0">
                        <div className='card-body p-3'>
                            <div>
                                <img className='home-center-series-videos-small'
                                    src={contents.med_image}
                                    alt="no Iamge" />
                            </div>

                            <div className='home-center-series-videos-small-text' >
                                 {/* {Moment(new Date(contents.created_at)).format("DD MMM")} */}
                                <h6>{contents.title}</h6>
                                <h6 style={{ fontWeight: "normal" }} >{contents.match_desc}</h6>
                            </div>
                        </div>
                    </div>
                </div>
        );
      }

    if (subType === "video") {
      return (
        <div>
          {contents.data.map((content, id) => (
            <div key={content.id} className="card mb-1">
              <div className="card-body p-3">
                <img
                  className="home-center-series-videos-large"
                  src={content.thumb}
                  alt="no Iamge"
                />
                <div className="home-center-videos-title">{contents.title}</div>
              </div>
            </div>
          ))}
        </div>
      );
    }

   if (subType === "news") {
      return (
        <div className="card">
          <div className="card-title">{contents.title}</div>
        </div>
      );
    }

    //======================= End Articles =====================================
    if (subType === "article") {
      return (
        <div>
          {contents.data.map(news => (
            <div key={news.id} className="card mb-1">
              <div className="card-body p-3">
                <img
                  className="home-center-series-videos-large"
                  src={news.image}
                  alt="no Iamge"
                />
                <div className="home-center-videos-title">
                  {news.title}
                  {/* <h6 style={{ fontWeight: "normal" }}>
                    {Moment(new Date(news.created_at)).format("DD MMM, YYYY")}
                  </h6> */}
                </div>
              </div>
            </div>
          ))}
        </div>
      );
    }
    //------------------------------- End Articles ----------------------------
  }
}

export default OtherContentsIdentifire;
