import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';

// import Feeds    from  '../home/homeCenterPanel1';
// import Series  from  '../series/series';
// import Videos  from  '../videos/videos';
// import Stories from  '../stories/news';
// import Ranking from  '../ranking/ranking';

import '../../assets/css/home/navigationBar.css';

class NavigationBar extends Component {

    render() {
        return (

            <div className='container'>
                <div className='row'>
                    <nav className="navbar navbar-expand-md" style={{ marginBottom: 6 + "px", backgroundColor: 'White', paddingRight:0+'px', paddingLeft:0+'px'}}>
                        <NavLink to="/">
                            <img style={{ width: 45 + "px", height: 45 + "px", marginRight : 50 + 'px' }}
                                src={require('../../assets/images/jazz_logo.png')}
                                alt=" " />
                        </NavLink>

                        <button className="navbar-toggler"
                            type="button"
                            data-toggle="collapse"
                            data-target="#navbarcontent"
                            area-controls="navbarcontent"
                            area-expanded="false"
                            area-label="Toggle Navigation">

                            <span className="navbar-toggler-icon">|||</span>
                        </button>


                        <div className="collapse navbar-collapse" id="navbarcontent" >

                            <ul className="navbar-nav">

                                <li className="nav-item">
                                    <NavLink className="nav-link"
                                        to="/"
                                        activeClassName="feeds-selected"
                                        activeStyle= {{fontWeight:600, color:'Black' }}
                                    > Feeds
                                      {/* <span className="sr-only">(current)</span> */}
                                    </NavLink>
                                </li>

                                <li className="nav-item">
                                    <NavLink className="nav-link"
                                        to="/series/allseries/live"
                                        activeClassName="series-selected"
                                        activeStyle= {{fontWeight:600, color:'Black' }}
                                        >Series
                                </NavLink>
                                </li>

                                <li className="nav-item">
                                    <NavLink className="nav-link"
                                        to={{
                                            pathname: '/videos',
                                            state: {
                                                selectedType: 'All_Videos',
                                                video: []
                                            }
                                        }}
                                        activeClassName="videos-selected"
                                        activeStyle= {{fontWeight:600, color:'Black' }}
                                        >Videos
                                </NavLink>
                                </li>

                                <li className="nav-item">
                                    <NavLink className="nav-link" to={{
                                        pathname: '/stories',
                                        state: {
                                            objSelectedStory: null,
                                            selectedType: 'Latest_News',
                                            showFull: false
                                        }
                                    }}
                                    
                                    activeClassName="stories-selected"
                                    activeStyle= {{fontWeight:600, color:'Black' }}
                                    
                                    >Stories
                                </NavLink>
                                </li>

                                <li className="nav-item">
                                    <NavLink className="nav-link"
                                        to="/ranking"
                                        activeClassName="ranking-selected"
                                        activeStyle= {{fontWeight:600, color:'Black' }}
                                        >Rankings
                                    </NavLink>
                                </li>

                                <li className="nav-item">
                                    <NavLink className="nav-link"
                                        to="/login"
                                        
                                        activeClassName="login-selected"
                                        activeStyle= {{fontWeight:600, color:'Black' }}
                                        
                                        >LOGIN
                                </NavLink>
                                </li>

                            </ul>
                        </div>
                    </nav>

                    {/* <Route exact path="/"  component = {Feeds}/>  
                  <Route path="/series"  component = {Series}/> 
                  <Route path="/videos"  component = {Videos}/> 
                  <Route path="/Stories" component = {Stories}/> 
                  <Route path="/ranking" component = {Ranking}/>  */}

                </div>
            </div>


        );
    }
}

export default NavigationBar;




