import React, { Component } from 'react';
import '../../assets/css/home/homeCenterPanel.css';
//import axios from 'axios';

class HomeCenterPanel extends Component {

    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            liveMatches : []
        };
    }

    componentDidMount() {

        fetch("http://54.245.34.7:8080/api/v2/main/live_matches")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        liveMatches: result
                    });
                },

                // Handling Erro
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }

    render() {

        const { error, isLoaded, liveMatches } = this.state;

        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {
            return (
                <div>
                    {/* <div className="card">
                        <img src={liveMatches.card_display} alt="no Image" />
                    </div> */}

                    {liveMatches.live_matches.map(match => (
                        <div key={match.id}>
                        
                            <div className="card">
                               <div className="card-header p-0 m-0"> 
                                   <p><img className = "card-img-top p-0" src={match.banner_url} alt="no Image" /></p>
                               </div>
                               
                                <div className="card-block px-1">
                                     <h6 className="card-title">{match.title}</h6> 
                                     <p>{match.format} {match.live_match_stat}</p>
                                </div>     
                        
                                <div className="card-text"> 
                                  <p>{match.teamA.name} ({match.teamA.short_name})</p>
                                  <p><img src={match.teamA.flag_file_name} alt="no Image" /></p>
                               </div> 
                          
                           </div>
                        </div>
                    ))}
                </div>
           
            );
        }
    }
}


export default HomeCenterPanel;


