import React, { Component } from 'react';
import {Link }              from 'react-router-dom'; 
import { library } from '@fortawesome/fontawesome-svg-core';
import { faPlayCircle } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import '../../assets/css/common/videoCards.css';          

library.add(faPlayCircle);

class HomeRightVideoHeighLights extends Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            videoHeighLights: [],
        };
    }

    componentDidMount() {

        fetch("https://back.cricwick.net/api/v1/user_api/home_videos?telco=mobilink")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        videoHeighLights: result
                    });
                },

                // Handling Erro
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }

    render() {
        const { videoHeighLights, error, isLoaded } = this.state;
        
        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {

            return (
              <div className="card p-0">
                <h6 className="card-header p-2">
                  VIDEO HIGHLIGHTS
                </h6>
                {videoHeighLights.timeline_videos.map(
                  (video, i) => (
                    <div key={video.id} className="card-body p-2">
                      <div className="container">
                        <div className="row">
                          <Link to="">
                            <div className="col-3 card-col-for-image">
                              <img
                                className="card-image-small"
                                src={video.thumb}
                                alr="Image not available"
                                alt="NoIamge"
                              />

                              <div className="play-button-small">
                                <FontAwesomeIcon
                                  color="white"
                                  icon={"play-circle"}
                                  size="4x"
                                />
                              </div>
                            </div>

                            <div className="col-9 card-col-for-text">
                              <span className="home-right-panel-matchdesc-text">
                                {video.match_desc}
                              </span>{" "}
                              <br />
                              {video.title}
                            </div>
                          </Link>
                        </div>
                      </div>
                    </div>
                  )
                )}
              </div>
            );
        }
    }
}


export default HomeRightVideoHeighLights;
