import React, { Component } from 'react';
import http from "../../services/httpService";
//import {userAuthentication}  from "../../services/userService";
import * as authService from "../../services/authService";
import Input from "../common/input";
import config from '../../config.json';


class Login extends Component {
    constructor(props) {
        super()
        this.state = {
            errors: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            userData: [],
            baseUrl: 'https://staging.cricwick.net:13002/api',
            page: 1,
            perPage: 10,
            account: { pin: "", userid: "", username: "" }
        }

    }

    componentDidMount() {
        // this.loadSeriesRecords();
      //  console.log("url : ", config.telco_app_load);
    }

    loadSeriesRecords = async () => {
        const { account} = this.state;

        //login_or_sendpin?telco=mobilink&phone=03001111111&sub_type='

        const Url = this.state.baseUrl + '/'
            + 'login_or_sendpin?telco=' + config.telco
            + '&phone=' + account.username 
            + '&sub_type='

        console.log("Series : ", Url);
        
        try {
            await http.get(Url).then(response => {
                this.setState({ userData : response.data });
            });
        }
        catch (err) {
            console.log("Error", err);
            window.location.href = '/404notfound';
        };
    }

    //jwt-decode
    //import jwtDecode from 'jwt-decode'
    handleSumbit = async (e) => {
        e.preventDefault();
        const { account } = this.state;
        console.log("Submit Clicked",account);

        const Url = this.state.baseUrl + '/'
        + 'login_or_sendpin?telco=' + config.telco
        + '&phone=' + account.username 
        + 'sub_type='

        console.log(Url);


        try {
           // const { data: jwt } = await authService.login(account.username);
            const { data } = await authService.login(Url);
            console.log(data);

            localStorage.setItem('token', "test");
            this.props.history.push('/');
        }
        catch (ex) {
            if (ex.response && ex.response.status === 400) {
                const errors = { ...this.state.errors };
                errors.username = ex.response.data;
                this.setState({ errors });
            }
        }

        // try {
        //     await userService.userAuthentication(this.state.account);
        // } 
        // catch (ex) {
        //     if (ex.response && ex.response.status === 400){
        //       const errors = { ...this.state.errors };
        //       errors.username =  ex.response.data;
        //       this.setState({errors});
        //     }
        // }        
        //console.log("Login : ", e.current);


    }

    handleChange = ({ currentTarget: input }) => {
        const account = { ...this.state.account };
        account[input.name] = input.value;
        this.setState({
            account: account
        })
    }

    render() {
        const { account } = this.state;
        return (
            <div className='card p-4'>
                <div>
                    <form onSubmit={this.handleSumbit}>
                        <Input name='username'
                            id='username'
                            value={account.username}
                            onChange = {this.handleChange}
                            label="Please Enter Your Number"
                            placeHolder="03xx xxxx xxx"
                            focus={true}
                        />
                        <button
                            className="btn btn-primary mt-3"
                            type="submit">Continue
                        </button>
                    </form>
                </div>

                <div style={{paddingTop: 30 + 'px'}} >
                    <img className="card-img-bottom"
                        //  src={config.telco_app_load} alt='NoImage'/>
                        src={require('../../assets/telcos/img-download.jpg')} alt='NoImage' />
                </div>

            </div>
        );
    }
}

export default Login;
