        console.log(allSeries);
    //    console.log(allGenerics);
        allSeries.map((series,i)=>{
          //  console.log(series);
          // console.log("=========Series Start =============");
            series.data.map((matches,i)=>{
                if (matches.type==='matches'){
                       console.log( "Matches : ", matches);
                       component = <Matches matches={matches} seriesObj={series.series_obj}/> 
                       return (component);
                }
                  
                if (matches.type==='news'){
                    matches.data.map((match,i)=>{
                       console.log("News : ", match);
                       component = <News news={matches}/>
                       return (component);
                    })   
                }

                if (matches.type==='videos'){
                    matches.data.map((match,i)=>{
                       //console.log("Videos : ",match);
                       return 'videos';
                    })   
                }

                if (matches.type==='tweets'){
                    matches.data.map((match,i)=>{
                       //console.log("Tweets : ",match);
                       return 'tweets';
                    })   
                }

                if (matches.type==='article'){
                    matches.data.map((match,i)=>{
                       //console.log("Articles : ",match);
                       return 'articles';
                    })   
                }
                  return '';
            })
            console.log("================= Series Finised=============> ",series.type);   
        });

        return component;
       }        
}
  
