import React, { Component } from 'react';
//import config from '../../config.json';
//import http from '../../services/httpService'
import {NavLink, Route} from 'react-router-dom';
//import http from 'superagent';
import SeriesSummary from './seriesSummary';
import MatchSchedules from './matchSchedules.jsx';
import PointsTable from '../common/pointsTable.jsx';
import FeaturedVideos from './featuredVideos';

import '../../assets/css/common/shortMenu.css';

// import '../../assets/css/home/homeCenterPanel.css';

class FeaturedSeries extends Component {
  
  constructor(props) {
    super(props);
   
    //const {viewable_type, viewable_id, selectedType, seriesTitle, tag_id, video_id, video , telco } = this.props.location.state;
    const {viewable_type, viewable_id, selectedType, seriesTitle, match} = this.props;
   
    this.state = {
      viewable_type : viewable_type,
      viewable_id   : viewable_id,
      match         : match, 
      // selectedType  : selectedType,
      // seriesTitle   : seriesTitle,
      // video_id      : video_id,
      // tag_id        : tag_id,    
      // telco         : telco,
      // video         : video, 
      page          : 1
    };
  }

  //  RECEVING PARAMETERS:-  
  //  viewable_type : 'series',
  //  viewable_id   : series.id,
  //  page          : page,
  //  telco         : telco

  //   let Url = 'https://back.cricwick.net/api/v2/view_lists/get_list_items_from_viewable'
  //   Url = Url + `?viewable_type=${series}`
  //             + `&viewable_id=${seriesId}`
  //             + `&page=${page}`
  //             + `&telco=${telco}`

  componentWillReceiveProps = async (nextProps) => {
    //  console.log( "Component Will Recevie props - selectedType : "     ,nextProps.location.state.selectedType);
    //  console.log( "Component Will Recevie props - objSelectedStory : " ,nextProps.location.state.objSelectedStory);
    //  console.log( "Component Will Recevie props - showFull   : " ,nextProps.location.state.showFull);
    
    // await this.setState(() => {
    //           return {
    //             viewable_type: nextProps.location.state.viewable_type,
    //             viewable_id: nextProps.location.state.viewable_id,
    //             selectedType: nextProps.location.state.selectedType,
    //             seriesTitle: nextProps.location.state.seriesTitle,
    //             tag_id: nextProps.location.state.tag_id,
    //             video_id: nextProps.location.state.video_id,
    //             video: nextProps.location.state.video,
    //           }
    // });
  }


  // componentDidMount = async () => {
  //   const {selectedType} = this.props.location.state;
  //   this.setState(()=>{ return{selectedType : selectedType} });
  // }

  
  // handleClick = async (selectedType) => {
  //   if (!selectedType) {
  //        await this.setState(()=>{ return{selectedType: 'summary'}} );
  //   } else {
  //        await this.setState(()=>{ return { selectedType: selectedType} });
  //   }
  // }

  //  https://back.cricwick.net/api/v2/view_lists/
  //  get_list_items_from_viewable?viewable_type=series&
  //  viewable_id=156&page=1&telco=mobilink

  render() {

    // const { viewable_type, viewable_id, selectedType, video ,video_id, tag_id, page, seriesTitle} = this.state;
    const {match, viewable_type, viewable_id} = this.state;
    console.log(match);
    
    return (
        <div>

          {/* Rendering Series Menu */}
          <div className="card card-block p-3 mb-1">
             <h6 className='menu-buttons-title-heading'>{match.params.title}</h6> 
            
            {/* <span className='short-menu-buttons'>
              <button className="btn btn-default mr-2" onClick={() => this.handleClick("summary")}>
                Summary
              </button>
              <button className="btn btn-default mr-2" onClick={() => this.handleClick("schedule")}>
                Schedule
              </button>
              <button className="btn btn-default mr-2" onClick={() => this.handleClick("videos")}>
                Videos
              </button>
              <button className="btn btn-default mr-2" onClick={() => this.handleClick("point_table")}>
                Point Table
              </button>
            </span> */}

            <span className='short-menu-buttons'>
                        <NavLink  className='btn btn-default' to={`${match.url}/summary`}>Summary</NavLink>
                        <NavLink  className='btn btn-default' to={`${match.url}/schedules`}>Schedules</NavLink>
                        <NavLink  className='btn btn-default' to={`${match.url}/videos`}>Videos</NavLink>
                        <NavLink  className='btn btn-default' to={`${match.url}/pointstable`}>Points Table</NavLink>
            </span>

          </div>
                   <Route
                                path={`${match.url}/summary`}
                                render={(props) => <SeriesSummary {...props} viewable_type={viewable_type} viewable_id={viewable_id} selectedType="summary" />}
                   />

        </div>  
          
                 
                 


            // Render Point Table

            // {selectedType === "point_table" &&
            // <div>
            //   <PointsTable viewable_id={viewable_id} />
            // </div>

            // {/* Render Match Schedules  */}
            // {/* {selectedType === "schedule" &&
            //   <div>
            //     <MatchSchedules viewable_id={viewable_id} page={page} />
            //  </div>} */}

            // {/* Temprary Division Defined for  */}
            // {/* {selectedType === "videos" &&
            // <div>
            //   <FeaturedVideos video={video} viewable_id={viewable_id} page={page} />
            //  </div>} */}
            // {/* End of Temprary Division */}

            // {/* {selectedType === "summary" &&
            //  <div>
            //   <SeriesSummary viewable_type={viewable_type} viewable_id={viewable_id} video_id={video_id} tag_id={tag_id} page={page} />
            // </div>}
            // </div> */}

      );
    }
  }

export default FeaturedSeries;
