import React, { Component } from 'react';
import request from 'superagent';
import Moment from 'moment';
import Loading from '../common/loading';
import DetailedNews from './detailedNews';
import { Link } from "react-router-dom";

import '../../assets/css/videos/videos.css';
import '../../assets/css/common/videoCards.css'

class LatestNews extends Component {

    constructor(props) {
        super(props)
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            seriesData: [],
            baseUrl: 'https://back.cricwick.net/api',
            page: 1,
            perPage: 5,
            type: '',
            // news      : props.objSelectedStory,
            // showFull  : props.showFull
        }
    }

    componentDidMount = async () => {
        console.log("Propertie Latest News : ", this.porps);
        
        await this.loadSeriesRecords();
    }

    componentWillReceiveProps = async (nextProps) => {

        // console.log( "Component Will Recevie props - selectedType : "     , nextProps.selectedType);
        // console.log( "Component Will Recevie props - objSelectedStory : " ,nextProps.objSelectedStory);
        // console.log( "Component Will Recevie props - showFull   : "      ,nextProps.showFull);

        //  await this.setState({
        //       news             :  nextProps.objSelectedStory,
        //       showFull         :  nextProps.showFull,
        //  });
    }

    loadSeriesRecords = async () => {
        const { page, perPage, baseUrl } = this.state;

        // 1) All Videos :
        //    Base URL =  https://back.cricwick.net/api/news?page=1&page_size=6
        //    Composed =  /news?page=1&page_size=5

        let Url = baseUrl
            + '/'
            + 'news'
            + '?'
            + 'page=' + page
            + '&'
            + 'page_size='
            + perPage

        console.log(Url);

        await request
            .get(Url)
            .set('Accept', 'application/json')
            .then(res => {
                this.setState(() => {
                    return {
                        isLoaded: true,
                        isLoading: false,
                        seriesData: res.body,
                        hasMore: (res.body.length < perPage)
                    }
                });
            })
            .catch(error => {
                this.setState({
                    isLoaded: false,
                    error
                });
            });
    }

    handleClick = (news) => {
        this.setState(() => {
            return {
                showFull: true, news: news
            }
        });
    }

    changeProps() {
        this.setState({ showFull: true });
        this.setState(() => {
            return {
                showFull: true
            }
        });
    }

    hideDetail = () => {
        this.setState((prevState) => {
            return {
                showFull: !prevState.showFull
            }
        });
    }

    render() {
        const { isLoaded, error, seriesData, showFull, news } = this.state;

        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<Loading />);
        } else {
            return (
                <div>

                    <div id='news' hidden={!showFull} style={{ cursor: 'pointer' }}
                        onClick={() => this.hideDetail()}>
                        {console.log('Selected News : ', news)}
                        {news &&
                            <DetailedNews news={news} />
                        }
                    </div>

                    {seriesData.data.map((news, i) => (
                        <div className="flex-d" key={news.id} >
                            <Link to={`/stories/news/${news.id}`}>
                                <div className="card mb-1 p-2" onClick={() => this.handleClick(news)} style={{ cursor: 'pointer' }} >
                                    <div className='card-body p-0'>
                                        <div className='container'>
                                            <div className='row'>
                                                <div className='col-3 card-col-for-image'>
                                                    <img className='card-image-small'
                                                        src={news.image}
                                                        alt="no Iamge" />
                                                </div>

                                                <div className='col-9 card-col-for-text'>
                                                    <span className='cardsmall-col-sub-heading-gray'> {news.series_name} </span>
                                                    <span className='cardsmall-col-sub-heading-gray' style={{ float: 'right' }}>
                                                        {Moment(new Date(news.created_at)).format("DD MMM,YYYY")}
                                                    </span>

                                                    <h6 className='cardsmall-col-sub-heading-black'>
                                                        {news.title}
                                                    </h6>
                                                    <div className='cardsmall-col-bodytext-black pt-1'>
                                                        <span dangerouslySetInnerHTML={{ __html: news.body }} />
                                                        {/* {console.log(news.body)} */}

                                                    </div>

                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </Link>
                        </div>
                    ))}
                </div>
            );
        }
    }
}

export default LatestNews;
