import React, { Component } from 'react';
import {Link}      from 'react-router-dom';
import { library } from '@fortawesome/fontawesome-svg-core';
import { faPlayCircle } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

library.add(faPlayCircle);

class HomeRightVideoHeighLights extends Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            videoHeighLights: [],
        };
    }

    componentDidMount() {

        fetch("https://back.cricwick.net/api/v1/user_api/home_videos?telco=mobilink")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        videoHeighLights: result
                    });
                },

                // Handling Erro
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }

    render() {
        const { videoHeighLights, error, isLoaded } = this.state;
        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {
            
            console.log(videoHeighLights)

            return (
                <div className="card p-0">
                    <h6 className='cards-top-heading-black'>VIDEOS HEIGHLIGHTS</h6>
                    <hr className='hr-line'/>
                     {videoHeighLights.timeline_videos.map((video, i) => (

                            <div key={video.id} className="card-body p-1 pl-2">
                               <Link to={{ pathname:'/leftPanel', 
                                           state:{ 
                                            viewable_id: video.match_obj.series_id.series_id,
                                            selectedType:'videos',
                                            tag_id:27,
                                            video_id: video.id         
                                           }
                                }}> 
                               <div className='container'>
                                 <div className='row'>
                                     <div className='col-5 card-col-for-image'>
                                         <img className='card-image-xs'
                                             src={video.thumb} alr="Image not available" alt='NoIamge' />

                                         <div className='play-button-small'>
                                             <FontAwesomeIcon color="white"
                                                 icon={"play-circle"}
                                                 size="3x" />
                                         </div>

                                     </div>

                                     <div className='col-7 card-col-for-text-xs'>
                                            <h6 className='cardsmall-col-sub-heading-gray-xs'>
                                                 {video.match_desc}
                                            </h6>
                                            <p className='cardsmall-col-bodytext-black'>
                                               {video.title}
                                            </p>
                                     </div>

                                 </div>
                             </div>
                            </Link> 
                        </div>
                    ))}
                </div>

            );
        }
    }
}


export default HomeRightVideoHeighLights;
