import React, { Component } from 'react';

class Match extends Component {
    constructor(props) {
        super(props)
        this.state = {
            match: '',
            matchType: '',
        }
    }

    render() {
        const { match, matchType } = this.props;

        if (matchType === 'matches') {
            return (
                <div>
                  {match.teamA.name} <br/>
                  {match.teamA.name}
                </div>
            )
        }

        if (matchType === 'news') {
            return (
                <ul>
                    <li>{match.title}</li>
                </ul>
            )
        }

        if (matchType === 'videos') {
            return (
                <div>videos</div>
            )
        }

        if (matchType === 'articles') {
            return (
                <div>Articles</div>
            )

        }

        return (
            <div>
                       Other than Match
                        News
            </div>
        )
    }
