import React, { Component } from 'react';
import http from 'superagent';
import ScheduleMatches from '../common/scheduleMatches';
import '../../assets/css/series/series.css';
import LiveMatch from '../common/liveMatch';
import '../../assets/css/summary/summary.css';

class Series extends Component {
    constructor(props) {
        super(props)
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            seriesData: [],
            selectedType: 'summary',
            baseUrl: 'https://back.cricwick.net/api',
        };

    }

    componentDidMount() {
        this.loadSeriesRecords();
        // this.state.seriesData['live_series'].map((item,i)=>(
        //       console.log(item)
        // ));
        //console.log( "From Component Did Mount : " , this.state.seriesData);
    }

    loadSeriesRecords = async () => {
        const { baseUrl } = this.state;
        const { match } = this.props.location.state;

        // console.log("Match from Parent Object : " , match);
        // fetch("http://back.cricwick.net/api/v3/view_lists/get_by_name?view=home")
        //Home Series Api  

        const Url = baseUrl + '/'
            + 'v3'
            + '/'
            + 'matches'
            + '/'
            + match.id
            + '/'
            + 'summary'

        console.log(Url);
        //console.log(hasMore);

        http
            .get(Url)
            .set('Accept', 'application/json')
            .then(res => {
                this.setState({
                    isLoaded: true,
                    isLoading: false,
                    seriesData: res.body,
                });
            })
            .catch(error => {
                this.setState({
                    isLoaded: false,
                    error
                });
            });
    }

    handleClick = (selectedType) => {

        if (!selectedType) {
            this.setState({ selectedType: 'summary' });
        } else {
            this.setState({ selectedType: selectedType });
        }

    }

    renderUpdatedSummary=(innings)=>{

        return (
            <div>
                {innings.map((inning, i) => (
                    <div>
                        {inning.innings_order === i+1 &&
                            <div> 
                                {'CRR :'} {inning.run_rate}
                                {' -- '}
                                {'Last WKT:'} {}   
                            </div>
                        }
                    </div>
                ))
                }
            </div>
            //=====================
        );


    }


    renderPartnerShip=(objPartnerShip)=>{
        return (
            <div>
                <div>
                    {console.log(objPartnerShip.batsman_1)}
                    {console.log(objPartnerShip.batsman_1)}
                </div>

                <table className='table table-striped table-condensed table-hover table-borderless rank-table'>
                    <thead>
                        <tr>
                            <th>BATSMEN</th>
                            <th>R</th>
                            <th>B</th>
                            <th>4s</th>
                            <th>6s</th>
                            <th>SR</th>
                        </tr>
                    </thead>
                    <tbody>
                            {/* Batsman  - 1 Details */}
                            <tr>
                                <td>
                                    <img className='team-flag' 
                                         src={objPartnerShip.batsman_1.display_picture_url} 
                                         alt='noImage' />
                                   {objPartnerShip.batsman_1.player.name}
                                   {objPartnerShip.batsman_1.batsman.on_strike === 1 ? '*' : ''}
                                </td>

                                <td>{objPartnerShip.batsman_1.batsman.runs_scored}</td>
                                <td>{objPartnerShip.batsman_1.batsman.balls_played}</td>
                                <td>{objPartnerShip.batsman_1.batsman.boundry_4s_scored}</td>
                                <td>{objPartnerShip.batsman_1.batsman.boundry_6s_scored}</td>
                                <td>{objPartnerShip.batsman_1.batsman.strike_rate}</td>
                            </tr>

                            {/* Batsman  - 2 Details */}
                            <tr>
                                <td>
                                    <img className='team-flag' 
                                         src={objPartnerShip.batsman_2.player.display_picture_file_name} 
                                         alt='noImage' />
                                       {objPartnerShip.batsman_2.player.name}
                                       {objPartnerShip.batsman_2.batsman.on_strike === 1 ? '*' : ''}
                                </td>

                                <td>{objPartnerShip.batsman_2.batsman.runs_scored}</td>
                                <td>{objPartnerShip.batsman_2.batsman.balls_played}</td>
                                <td>{objPartnerShip.batsman_2.batsman.boundry_4s_scored}</td>
                                <td>{objPartnerShip.batsman_2.batsman.boundry_6s_scored}</td>
                                <td>{objPartnerShip.batsman_2.batsman.strike_rate}</td>
                            </tr>



                        ))}
                    </tbody>
                </table>



            </div>
        );


    } 


    render() {
        const { error, isLoaded, seriesData } = this.state;

        console.log(seriesData);

        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {

            return (
                <div>
                    <div className='card card-block mb-1'>
                        <span>
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('Summary')} >Summary</button>
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('Scorecard')}>Scorecard</button>
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('Videos')}>Videos</button>
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('Ball_by_Ball')}>Ball by Ball</button>
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('Squads')}>Squads</button>
                        </span>
                    </div>

                    <div className='card p-3 mb-1'>
                        <LiveMatch match={seriesData.match} />
                    </div>
                   
                    <div>
                         {this.renderUpdatedSummary(seriesData.match.innings)}
                    </div>

                    <div>
                           {this.renderPartnerShip(seriesData.match.partnership)}
                    </div> 

                </div>
            );
        }
    }
}

export default Series;
