import React, { Component } from 'react';
import config from '../../config.json';
//import http from '../../services/httpService'
import http from 'superagent';
import Articles from '../common/articles';
import LiveMatch from '../common/liveMatch';
import News from '../common/news';
import Tweets from '../common/tweets';
import Videos from '../common/videos';
import MatchSchedules from './matchSchedules.jsx';


class FeaturedSeries extends Component {
    constructor(props) {
        super(props);
        this.state = {
            pointTable: false,
            seriesData: [],
            isLoading: true,
            isLoaded: false,
            hasMore: true,
            selectedType: 'summary',
            error: null,
            page: 1,
        };
    }

    //  RECEVING PARAMETERS:-  
    //  viewable_type : 'series',
    //  viewable_id   : series.id,
    //  page          : page,
    //  telco         : telco

    //   let Url = 'https://back.cricwick.net/api/v2/view_lists/get_list_items_from_viewable'
    //   Url = Url + `?viewable_type=${series}`
    //             + `&viewable_id=${seriesId}`
    //             + `&page=${page}`
    //             + `&telco=${telco}`

    componentDidMount = async () => {
        this.getSeriesRecords();
    }

    getSeriesRecords = () => {
        const { viewable_type, viewable_id, page } = this.props.location.state;

        const Url = config.apiEndPoint
            + '/'
            + 'v2'
            + '/'
            + 'view_lists'
            + '/'
            + 'get_list_items_from_viewable'
            + '?'
            + 'viewable_type=' + viewable_type
            + '&'
            + 'viewable_id=' + viewable_id
            + '&'
            + 'page=' + page
            + '&'
            + 'telco=' + config.telco

        console.log(Url);

        http
            .get(Url)
            .set('Accept', 'application/json')
            .then(res => {
                this.setState({
                    isLoading: false,
                    isLoaded: true,
                    seriesData: res.body,
                });

            }).catch(error => {
                this.setState({
                    isLoaded: false,
                    error: error
                });

            })

    }

    handleClick = (selectedType) => {

        if (!selectedType) {
            this.setState({ selectedType: 'summary' });
        } else {
            this.setState({ selectedType: selectedType });
        }

    }

    //  https://back.cricwick.net/api/v2/view_lists/
    //  get_list_items_from_viewable?viewable_type=series&
    //  viewable_id=156&page=1&telco=mobilink

    render() {
        const { seriesData, error, isLoading } = this.state;
        const { viewable_id, page } = this.props.location.state;

        if (error) {
            return (<div> Error :  {error.message} </div>)
        } else if (isLoading) {
            return (<div> Loading Data... </div>)
        } else {
            
            console.log(" Series Data :" ,seriesData)

            return (
                <div>
                    <div className='card card-block mb-1'>
                        <h6>{seriesData.title}</h6>
                        <span>
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('summary')} >Summary</button>
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('schedule')}>Schedule</button>
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('video')}>Videos</button>
                            <button className='btn link-button btn-link mr-2 btn-sm' onClick={() => this.handleClick('point_table')}>Point Table</button>
                        </span>
                    </div>


                    {/* Temprary Division Defined for Test */}
                    {this.state.selectedType === 'schedule' &&
                        <div>
                            <MatchSchedules viewable_id={viewable_id} page={page} />
                        </div>
                    }
                    {/* End of Temprary Division */}



                   {this.state.selectedType === 'summary' &&
                        <div>
                            {seriesData.map((matches, i) => (
                                <div key={i}>
                                    <div className='card pt-2 pl-2 mb-1'>
                                        <h6 className='card-title'>
                                            {matches.title}
                                        </h6>
                                    </div>
                                    {matches.data.map((match, i) => (
                                        <div key={i}>
                                            {match.type === 'match_object' &&
                                                <div className='card p-3 mb-1'>
                                                    <LiveMatch match={match.data} />
                                                </div>
                                            }

                                            {match.type === 'videos' &&
                                                <div>
                                                    <Videos match={match.data} firstTitle={true} />
                                                </div>
                                            }


                                            {match.type === 'news' &&
                                                <div className='card p-3 mb-1'>
                                                    <News match={match.data} />
                                                </div>
                                            }

                                            {match.type === 'articles' &&
                                                <div>
                                                    <Articles match={match.data} />
                                                </div>
                                            }

                                            {match.type === 'tweets' &&
                                                <div>
                                                    <Tweets match={match.data} />
                                                </div>
                                            }

                                        </div>
                                    ))}
                                </div>
                            ))}
                        </div>
                    }

                </div>

            );
        }
    }
}

export default FeaturedSeries;
