import React, { Component } from 'react';
import request from 'superagent';
import Moment from 'moment';
import DetailedNews from './detailedNews';
import '../../assets/css/videos/videos.css';


class LatestNews extends Component {

    constructor(props) {
        super(props)
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            selectedType: 'Latest_News',
            seriesData: [],
            baseUrl: 'https://back.cricwick.net/api',
            page: 1,
            perPage: 5,
            news: [],
            showFull: false,
            hideOther: false

        }


    }


    componentDidMount = async () => {
        await this.loadSeriesRecords();
    }

    loadSeriesRecords = async () => {
        const { page, perPage, baseUrl } = this.state;

        // 1) All Videos :
        //    Base URL =  https://back.cricwick.net/api/news?page=1&page_size=6
        //    Composed =  /news?page=1&page_size=5


        let Url = baseUrl
            + '/'
            + 'news'
            + '?'
            + 'page=' + page
            + '&'
            + 'page_size='
            + perPage

        console.log(Url);

        await request
            .get(Url)
            .set('Accept', 'application/json')
            .then(res => {
                this.setState({
                    isLoaded: true,
                    isLoading: false,
                    seriesData: res.body,
                    hasMore: (res.body.length < perPage)
                });

            })
            .catch(error => {
                this.setState({
                    isLoaded: false,
                    error
                });
            });
    }

    handleClick = (news) => {
        this.setState({
            showFull: true, news
        });
    }

    hideDetail = () => {
        this.setState({
            showFull: !this.state.showFull, hideOther: true
        });
    }

    renderDetailedNews = async (news) => {
        const newsDetail =  await this.state.seriesData.data.filter(x => x.id === news.id)
        console.log(newsDetail);
    }

    render() {
        const { isLoaded, error, seriesData, showFull, news } = this.state;
        const { selectedNews } = this.props;
      
        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {
            return (
                <div>
                    
                     {selectedNews &&
                       <div style={{ cursor: 'pointer' }}>
                           {this.renderDetailedNews(selectedNews)}
                       </div>
                    }

                    <div id='news' hidden={!showFull} style={{ cursor: 'pointer' }}
                        onClick={() => this.hideDetail()}>
                           {this.renderDetailedNews(news)}
                    </div>

                    {seriesData.data.map((news, i) => (
                        <div className="flex-d" key={news.id} >
                            <div className="card mb-1 p-2" style={{ cursor: 'pointer' }}
                                onClick={() => this.handleClick(news)}>
                                <div className='card-body p-0'>
                                    <div>
                                        <img className='card-videos-small'
                                            src={news.image}
                                            alt="no Iamge" />
                                    </div>

                                    <div>
                                        <div className='card-videos-small-date'>
                                            <span> {news.series_name} </span>
                                            <span> {Moment(new Date(news.created_at)).format("DD MMM,YYYY")} </span>
                                        </div>

                                        <div className='card-video-title-text' >{news.title}</div>
                                        <div className='card-videos-body-text' style={{ width: 100 + "%" }} > {news.body.substr(5, 200)} </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            );
        }
    }
}

export default LatestNews;
