import React, { Component } from 'react';
import LatestNews from './latestNews';
import Articles from './articles';

class Stories extends Component {

    constructor(props) {
        super(props)
        this.state = {
            selectedType: 'Latest_News'
        }
    }

    handleClick = (selectedType) => {
        this.setState({ selectedType: selectedType });
        }

    render() {
        const { selectedType} = this.state;
        const {selectedNews} = this.props.location.state;

        console.log(selectedNews);

        return (
            <div>
                <div className='card'>
                    <span>
                        <button className='btn btn-link' onClick={() => this.handleClick('Latest_News')}>Latest News</button>
                        <button className='btn btn-link' onClick={() => this.handleClick('Articles')}>Articles</button>
                    </span>
                </div>

                {selectedType === 'Latest_News' &&
                    <div>
                        <LatestNews/>
                    </div>
                }

                {selectedType === 'Articles' &&
                    <div>
                        <Articles/>
                    </div>
                }

            </div>
        );

    }
}

export default Stories;
