import React, { Component } from 'react';
import '../../assets/css/home/homeCenterPanel.css';
import Match from './match';
//import axios from 'axios';

class HomeCenterPanel extends Component {

    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            homeContents: [],
            tempContents: [],
            series: [],
            match : null,
        };
    }

    componentDidMount() {

        // fetch("http://back.cricwick.net/api/v3/view_lists/get_by_name?view=home")
        //Home Series Api  
        fetch("http://back.cricwick.net/api/v3/view_lists/get_by_name?view=home&web_user=1&page=1")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        homeContents: result
                    });
                },

                // Handling Erro
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }

    renderMatchTeamATag(match, id){
        let teamA_Id =  match.team_1_id
        let tagText ='';
        console.log(teamA_Id)
        //let scoreI1 = '';
        //let scoreI2 = '';

       if(!match.innings)
           return tagText='';   

        // match.innings.map((score,i)=>{
        //    console.log(score)    
        // })
        
        // if(match.innings.length > 0) {
        //   tagText = 
        //   <span> 
        //       {match.innings[0].runs}{ " "}/{ " "}
        //       {match.innings[0].wickets} {" "} 
        //       ({match.innings[0].overs}) 
        //   </span>
        // }

        return tagText;
    }

    renderMatchTeamBTag(match,id){
        let teamB_Id =  match.team_2_id
        let tagText ='';

        console.log(teamB_Id)      
       
        if(!match.innings)
           return tagText='';   
       
        // match.innings.map((score,i)=>{
        //     console.log(score)    
        //  })
         
        // if(!match.innings[1]){
        //    console.log(match.innings[1])
        //    tagText =  <span></span>
        //    return 
        // }   

        // if (match.innings.length >= 1) {
        //     tagText =
        //         <span>
        //             {match.innings[1].runs}{" "}/{" "}
        //             {match.innings[1].wickets} {" "}
        //             ({match.innings[1].overs})
        //         </span>
          
        // }
        return tagText;
   }

     
    render() {
        const { error, isLoaded, homeContents} = this.state;
        
        let tempContents1 =  homeContents.filter(x => x.type !== 'generic-home');
        //let genericHome =  homeContents.filter(x => x.type === 'generic-home');
        
        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {

        return(
          <div>
                {tempContents1.map((series, i) => (
                    <div key={i}>
                              <span className='header-text-series'>  
                                    {series.title} 
                              </span>
                       <div>
                            {series.data.map((matches, i) => (
                              <div key={i}>
                                   
                                    {/* Matches Title */}
                                     <div>
                                         { matches.type === 'matches' ? matches.title : '' }
                                    </div>
                                     
                                    {/* Render Series Contents Matches/Videos/Articles/Tweets etc */}
                                    {matches.data.map((match,i)=>(
                                         <div key={i}>
                                            <Match    match={match}
                                                  matchType={matches.type}/>
                                        </div>
                                    ))}  

                              </div>   
                            ))}
                        </div>
                    </div>     
              ))}
          </div> 
        )                 
      }
    }
}

export default HomeCenterPanel;
