import React, { Component } from 'react';
import {NavLink, Route}    from 'react-router-dom';
import http from 'superagent';
import MatchSummary         from './matchSummary';
import MatchVideos          from './matchVideos';
import MatchBallByBall      from './matchBallByBall';
import MatchSquads          from './matchSquads'; 
import ScoreCard            from './scoreCard';
import Loading              from '../common/loading';

class Summary extends Component {
  
    constructor(props) {
        super(props)
        const {match} =  this.props;
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            seriesData: [],
            over: null,
            match_id: match.params.match_id,  
            selectedType: match.params.selectedType,
            baseUrl: 'https://back.cricwick.net/api',
        };

    }

     componentDidMount= async () =>{
          this.loadSeriesRecords();       
     }

    loadSeriesRecords = async () => {
        const { baseUrl, telco, match_id } = this.state;
        // Series Summmary URL
        const Url = baseUrl + '/'
            + 'v3'
            + '/'
            + 'matches'
            + '/'
            + match_id
            + '/'
            + 'summary'
            + '?telco=' + telco

        console.log(Url);
        //console.log(hasMore);

        http
            .get(Url)
            .set('Accept', 'application/json')
            .then(res => {
                this.setState({
                    isLoaded: true,
                    isLoading: false,
                    seriesData: res.body
                });
            })
            .catch(error => {
                this.setState({
                    isLoaded: false,
                    error
                });
            });
    }

    render() {
        const {match_id, seriesData , error, isLoading } = this.state;
        const {match} =  this.props;
        
        const { } = this.state;

        if (error) {
          return <div> Error : {error.message} </div>;
        } else if (isLoading) {
          return (<Loading />)
        } else {       
          return (
                <div id='match_summary'>
                    <div className='card card-block pl-2 pt-3 pb-3 mb-1'>
                        <h6 className='menu-buttons-title-heading'>{seriesData.match.series.title}</h6>
                         <span className='short-menu-buttons'>
                            <NavLink className='btn btn-default' to={`${match.url}/summary  `}   >Summary      </NavLink>
                            <NavLink className='btn btn-default' to={`${match.url}/scorecard`}   >Scorecard    </NavLink>
                            <NavLink className='btn btn-default' to={`${match.url}/videos`}      >Videos       </NavLink>
                            <NavLink className='btn btn-default' to={`${match.url}/ballbyball`}  >Ball by Ball </NavLink>
                            <NavLink className='btn btn-default' to={`${match.url}/squads`}      >Squads      </NavLink>
                        </span>

                    </div>

                    <div>
                        <Route exact
                            path={`${match.url}`}
                            render={(props) => <MatchSummary {...props}   match_id= {match_id}  selectedType="summary" />}
                        />
                      
                        <Route strictly exact
                            path={`${match.url}/scorecard`}
                            render={(props) => <ScoreCard {...props}       match_id= {match_id}   selectedType="scorecard" />}
                        />
                        <Route strictly exact
                            path={`${match.url}/videos`}
                            render={(props) => <MatchVideos {...props}     match_id= {match_id}  selectedType="videos" />}
                        />
                        <Route strictly exact
                            path={`${match.url}/ballbyball`}
                            render={(props) => <MatchBallByBall {...props} match_id= {match_id}  selectedType="ballbyball" />}
                        />
                        <Route strictly exact
                            path={`${match.url}/squads`}
                            render={(props) => <MatchSquads {...props}     match_id= {match_id}  selectedType="ballbyball" />}
                        />
                    </div>  

                  </div>
            );
        }
    }
}

export default Summary;



