import React, { Component } from 'react';
import config from "../../config.json";
import AllVideos from './allVideos';
import Featured from './featured';
import Archives from './archives';
import '../../assets/css/videos/videos.css';

class Videos extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      isLoading: false,
      hasMore: true,
      seriesData: [],
      selectedType: this.props.location.state.selectedType,
      baseUrl: "https://back.cricwick.net/api/v1",
      page: 1,
      perPage: 5,
      telco: config.telco,
      video: this.props.location.state.video,
      tag_id: 27,
      video_id: "",
      title: "",
      matchDesc: "",
    };
  }

  componentWillReceiveProps = (nextProps) => {
    console.log('component Will Recevie Props NextProps :', nextProps.selectedType, nextProps.video);
    this.setState(() => {
      return {
        selectedType: nextProps.selectedType,
        video: nextProps.video
      };

    });
    // await this.handleClick(nextProps.video)
  };

  handleClick = (selectedType) => {
    if (!selectedType) {
      this.setState(() => {
        return { selectedType: "All_Videos" };
      });
    } else {
      this.setState(() => {
        return { selectedType: selectedType };
      });
    }
  };

  render() {
    const { selectedType, video } = this.state;

    console.log("Videos Selected type : ",selectedType);

    return (
      <div>
        <div className="card card-block pl-2 pt-3 pb-3 mb-1">
          <span className="short-menu-buttons">
            <button
              className='btn btn-default'
              onClick={() => this.handleClick("All_Videos")}
            >
              All Videos
            </button>

            <button
              className="btn btn-default"
              onClick={() => this.handleClick("Featured")}
            >
              Featured
            </button>

            <button
              className="btn btn-default"
              onClick={() => this.handleClick("Archives")}
            >
              Archives
            </button>
            
          </span>
        </div>

        {selectedType === "All_Videos" &&
          <div>
            {console.log("Running All video..")}
            <AllVideos selectedType={selectedType} />
          </div>
        }

        {selectedType === "Featured" &&
          <div>
             {console.log("Running Featured video..")}
            <Featured selectedType={selectedType} video={video} />
          </div>
        }

        {selectedType === "Archives" &&
            <div>
                {console.log("Running Featured video..")}
                <Archives selectedType={selectedType} video={video} />
            </div>
        }

      </div>
    );
  }
  //----------------------------------------------------------------------
}

export default Videos;
