import React, { Component } from 'react';
import http from 'superagent';
import '../../assets/css/summary/summary.css';
import '../../assets/css/series/series.css';

class ScoreCard extends Component {

    constructor(props) {
        super(props)
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            seriesData: [],
            over: null,
            selectedType: 'summary',
            baseUrl: 'https://back.cricwick.net/api',
        };

    }

    componentDidMount() {
        this.loadSeriesRecords();
    }


    loadSeriesRecords = async () => {
        const { baseUrl } = this.state;
        const { match } = this.props;

        // https://back.cricwick.net/api/2404

        const Url = baseUrl + '/'
            + match.id

        console.log(Url);
        //console.log(hasMore);

        http
            .get(Url)
            .set('Accept', 'application/json')
            .then(res => {
                this.setState({
                    isLoaded: true,
                    isLoading: false,
                    seriesData: res.body,
                });
            })
            .catch(error => {
                this.setState({
                    isLoaded: false,
                    error
                });
            });
    }

    renderBattingScoreCard = (innings) => (
        <div>
            {innings.map((inning, i )=>(
                inning.map((score, i )=>(
                    console.log(score)
                ))  
            ))}
        </div>
    );

    render() {
        const { error, isLoaded, seriesData } = this.state;

        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<div> Loading Data.... </div>);
        } else {
            return (
                  <div>
                       <div>
                          {this.renderBattingScoreCard(seriesData.innings)}
                       </div>


                  </div>
            );
        }
    }
}

export default ScoreCard;
