import React, { Component } from "react";
import { library }          from '@fortawesome/fontawesome-svg-core';
import { faPlayCircle }     from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon }  from "@fortawesome/react-fontawesome";
import  VideoCardLink       from '../common/videoCardLink';   
import OtherVideoCardLink from './otherVideoCardLink.jsx';;

import "../../assets/css/home/genericHome.css";

library.add(faPlayCircle);

class OtherContentsIdentifire extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  handleClick=() =>{

  } 

  render() {
    const { contents, typeVideo } = this.props;
    let subType = contents.sub_type;

    if (!contents.data) 
        return( <div> {" "} {contents.title}{" "} </div>);

    if (typeVideo === "video") {
      return( 
        <div>
          {contents.data.map((video , i) => (
            <div key={i}>
               <OtherVideoCardLink video={video} selectedType={'Featured'} left_Col={3} right_Col={9}/>
            </div>
          ))}
        </div>
      )
    }

    if (subType === "video") {
      return (
      <div>
          {contents.data.map((video, id) => (
             <OtherVideoCardLink video={video} selectedType={'Archives'} left_Col={3} right_Col={9}/>
          ))}
      </div> 

      );
    }

    if (subType === "news") {
      return <div>
        {contents.data.map(news => (
          <div key={news.id} className="card mb-1">
            <div className="card-body p-3">

              <div className='video-card-image-size-large'>

                <img src={news.image}
                  alt="no Iamge"
                />
                <div className='play-button'>
                  <FontAwesomeIcon color="white"
                    icon={"play-circle"}
                    size="6x"
                  />
                </div>
              </div>

              <div className="cardsmall-col-heading-black">
                {news.title}
              </div>

            </div>
          </div>
        ))}
      </div>;
    }

    //======================= End Articles =====================================
    if (subType === "article") {
      return <div>
        {contents.data.map(news => (
          <div key={news.id} className="card mb-1">
            <div className="card-body p-3 pb-0">

              <div className='video-card-image-size-large'>
                <img src={news.image}
                  alt="no Iamge"
                />
              </div>

              <div className="cardsmall-col-heading-black">
                {news.title}
              </div>

              {/* <h6 style={{ fontWeight: "normal" }}>
                        {Moment(new Date(news.created_at)).format("DD MMM, YYYY")}
                      </h6> */}
            </div>
          </div>
        ))}
      </div>;
    }
    //------------------------------- End Articles ----------------------------
  }
}

export default OtherContentsIdentifire;
