import React, { Component } from 'react';
import Moment               from 'moment';
import RenderMatch          from './renderMatch';
import {Link}               from 'react-router-dom';
// import { library }          from '@fortawesome/fontawesome-svg-core';
// import { faPlayCircle }     from "@fortawesome/free-solid-svg-icons";
// import { FontAwesomeIcon }  from "@fortawesome/react-fontawesome";
import VideoCardLink        from '../common/videoCardLink';

import '../../assets/css/home/homeCenterPanel.css';
import '../../assets/css/common/videoCards.css';
import VideoCardLarge from '../common/videoCardLarge';

// library.add(faPlayCircle);

class SeriesContentsIdentifire extends Component {
    constructor(props) {
        super(props)
        this.state = {
            match: '',
            matchType: '',
        }
    }

    
  
    
    //----------------------------------------------------------- 
    // This Method Print Series Title on the first card as header. 
    printMatchCardHeader(printSeriesTitle, seriesTitle) {
        if (printSeriesTitle === 0) {
            return <span className="card-title">
                       {seriesTitle}
                       <hr />
                    </span>
        }
        return;
    }


    render() {
        const { match, contentType, printSeriesTitle, seriesTitle } = this.props;
        if (contentType === 'matches') {
            return (
              <div>   
                    {match.match_state !== 'Scheduled' &&
                        <Link to={{ pathname: '/summary/', state: { match: match } }}>
                            <div className="card card-block mb-1" style={{ minHeight: 75 + "px" }}>
                                <div className="card-body p-3">
                                    {this.printMatchCardHeader(printSeriesTitle, seriesTitle)}
                                    <RenderMatch match={match} matchType={contentType} />
                                </div>
                            </div>
                        </Link>
                    }

                    {match.match_state === 'Scheduled' &&
                        <div className="card card-block mb-1" style={{ minHeight: 75 + "px" }}>
                            <div className="card-body p-3">
                                {this.printMatchCardHeader(printSeriesTitle, seriesTitle)}
                                <RenderMatch match={match} matchType={contentType} />
                            </div>
                        </div>
                    }
             </div>  

            );
        }

        if (contentType === 'news') {
            return (
                <div>
                    <ul>
                        <li className='news-list'  key={match.id}>
                            <Link to={{
                                pathname: '/stories',
                                state: {
                                    objSelectedStory: match,
                                    selectedType: 'Latest_News',
                                    showFull: true
                                }
                            }}>

                                {match.title}
                            </Link>
                        </li>
                    </ul>
                </div>
            )
        }


        //-------------------------------------------------------------------------------
        // 1) This Method Print Particular Series video's. the 1st card as Series Videos as header.
        // 2) Further remaining videos as small card image and text Side By Side format.   
        if (contentType === 'videos') {
            
            if(printSeriesTitle===0){
                return(<VideoCardLarge video={match}/>)
            }
            
            if(printSeriesTitle > 0){
                return(<VideoCardLink video={match} left_Col={3} right_Col={9} />)
            }

        }
         //-------------------------------------------------------------------------------


        if (contentType === 'article') {
            return (
                <div className="flex-d">
                    <div className="card mb-1 p-0">
                        <div className='card-body p-3'>
                            <div className='container'>
                                <Link to={{
                                    pathname: '/stories',
                                    state: {
                                        objSelectedStory: match,
                                        selectedType: 'Articles',
                                        showFull: true }}}>
                                        
                                    <div className='row'>
                                        <div className='col-9 card-col-for-text'>
                                            <h4 className="cardsmall-col-heading-black">
                                                {match.title}
                                            </h4>
                                            <h6 className='cardsmall-col-sub-heading-gray'>
                                                {Moment(new Date(match.created_at)).format("DD MMM, YYYY")}
                                            </h6>
                                        </div>

                                        <div className='col-3 card-col-for-image'>
                                            <img className='card-image-small'
                                                src={match.image}
                                                alt="no Iamge" />
                                        </div>
                                    </div>
                                </Link>
                            </div>
                        </div>
                    </div>
                </div>
            )
        }

        if (contentType === 'tweets') {

            return (
                <a href={match.url} target='_blank' rel='noopener noreferrer' >
                    <div className='card mb-1 tweets-card'>
                        <div className='card-body p-3'>
                            <span
                                dangerouslySetInnerHTML={{ __html: match.body }}>
                            </span>
                        </div>
                    </div>
                </a>
            )
        }

        return (
            <div>

            </div>
        )
    }
}

export default SeriesContentsIdentifire;
