import React, { Component } from "react";
import config from "../../config.json";
import Match  from 'mathjs';
//import http from '../../services/httpService'
import http from "superagent";
import Loading from '../common/loading';
import {Link} from 'react-router-dom';
import ScheduleMatches from "../common/scheduleMatches.jsx";
// import LiveMatch from '../common/liveMatch';
import '../../assets/css/common/scheduledMatches.css';

class MatchSchedules extends Component {
    constructor(props) {
        super(props);
        this.state = {
            pointTable: false,
            seriesData: [],
            isLoading: true,
            isLoaded: false,
            hasMore: true,
            selectedType: "Summary",
            error: null,
            page: 1,
            perPage: 0,
        };
    }
    
    //  RECEVING PARAMETERS:-
    //  viewable_type : 'series',
    //  viewable_id   : series.id,
    //  page          : page,
    //  telco         : telco
    
    //   let Url = 'https://back.cricwick.net/api/series_schedule/156/schedule?page=1'
    
    componentDidMount = async () => {
        window.addEventListener('scroll', this.handleScroll);
        this.loadSeriesRecords();
    };

    componentWillUnmount = async() => {
        window.removeEventListener('scroll', this.handleScroll);
    }

    loadSeriesRecords = async () => {
        const {viewable_id} = this.props;
        const {seriesData}   =  this.state; 
       
        const Url =
            config.apiEndPoint
            + "/"
            + "series_schedule"
            + "/"
            + viewable_id + // Series ID stored in ==> viewable_id
            "/" +
            "schedule" +
            "?" +
            "page=" + this.state.page;

        console.log(Url);

        await http
            .get(Url)
            .set("Accept", "application/json")
            .then(res => {
                this.setState({
                    isLoading: false,
                    isLoaded: true,
                    seriesData: {...seriesData, ...res.body},
                    hasMore: ((res.body.recent_matches.length +  res.body.upcoming_matches.length) <= 0),
                });
                // live_matches, recent_matches, upcoming_matches
                console.log("Length is : ", res.body.recent_matches.length +  res.body.upcoming_matches.length);
            })
            .catch(error => {
                this.setState({
                    isLoaded: false,
                    error: error
                });
            });
    };

    handleScroll = async() => {
        const { hasMore, page} = this.state;
        const offset = (document.documentElement.scrollTop + window.innerHeight);
        const height = document.documentElement.offsetHeight;

        if (!hasMore) {
            //console.log("offset : ", Math.ceil(offset));
            console.log("offset : ", Math.floor(offset));
            console.log("height : ", height);
            
            if (Math.floor(offset) === height) {
                console.log("Inside Scroll");
                console.log("HasMore : ", hasMore, " Page:", page);
                await this.setState({ page: this.state.page + 1 });
                await this.loadSeriesRecords();
            }
        } else {
            console.log('End of list....');
        }
    }


    //  https://back.cricwick.net/api/v2/view_lists/
    //  get_list_items_from_viewable?viewable_type=series&
    //  viewable_id=156&page=1&telco=mobilink

    render() {
        const { seriesData, error, isLoading } = this.state;

        if (error) {
            return <div> Error : {error.message} </div>;
        } else if (isLoading) {
            return (<Loading />);
        } else {
            return (
                <div onScroll={this.handleScroll}>
                    {seriesData['recent_matches'].map((match, i) => (
                        <div key={i} className='card p-3 mb-1'>
                                {match.match_state !== 'Scheduled'
                                    ?
                                    <Link to={{ pathname: '/summary/', state: { match: match } }}>
                                        <ScheduleMatches match={match} />
                                    </Link>
                                    :
                                    <ScheduleMatches match={match} />}
                                <hr />
                        </div>
                    ))}

                    <div>
                        {seriesData['upcoming_matches'].map((match, i) => (
                            <div ksy={i} className='card p-3 mb-1'>
                                <ScheduleMatches match={match} />
                            </div>
                        ))}
                    </div>

                    {seriesData['live_matches'].map((match, i) => (
                        <div key={i} className='card p-3 mb-1'>
                            {match.match_state !== 'Scheduled'
                                ?
                                <Link to={{ pathname: '/summary/', state: { match: match } }}>
                                    <ScheduleMatches match={match} />
                                </Link>
                                :
                                <ScheduleMatches match={match} />}
                        </div>
                    ))}
                </div>
            );
        }
    }
}

export default MatchSchedules;
