import React, { Component } from 'react';
import '../../assets/css/home/homeLeftPanel.css';

class HomeLeftDownPanel extends Component {

    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            updates: []
        }

    }


    componentDidMount() {
        fetch("http://back.cricwick.net/api/news")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        updates: result
                    });
                },

                // Handling Erro
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }

    render() {
        const { error, isLoaded, updates } = this.state;
        
        if (!isLoaded) {
            return (<div> Loading Data ....... Please wait......  </div>)
        } else if (error) {
            return (<div> error : {error.message} </div>)
        } else {
            console.log(updates)  
            return (
                <div>
                <div className="card card-block">
                    <h6 className="card-title p-1">Updates</h6>
                    <div className='card-body m-0 p-0'>
                        <ul className="list-group list-group-flush"> 
                            {updates.data.map(update => (
                                <li className='list-group-item' key={update.id}> {update.title} </li>
                            ))}
                        </ul>
                    </div>
                </div>
            </div>
            );
        }
    }
}

export default HomeLeftDownPanel;
