import React, { Component } from "react";
import Moment from "moment";
import {Link} from 'react-router-dom';
import { library } from '@fortawesome/fontawesome-svg-core';
import { faPlayCircle } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";


import "../../assets/css/home/genericHome.css";
import "../../assets/css/common/videoCards.css";

library.add(faPlayCircle);

class OtherContentsIdentifire extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  render() {
    const { contents, typeVideo } = this.props;
    let subType = contents.sub_type;

    if (!contents.data) return <div>
      {" "}
      {contents.title}{" "}
    </div>;

    if (typeVideo === "video") {
      return <div>
        {contents.data.map(video => (
          <div key={video.id} className="flex-d">
            <div className="card mb-1 p-0">
              <div className="card-body p-3">

                <div className='container'>
                  <div className='row'>

                    <Link to=''>
                      <div className='col-3 card-col-for-image'>
                        <img className='card-image-small'
                          src={video.med_image}
                          alt="no Iamge"
                        />

                        <div className='play-button-small'>
                          <FontAwesomeIcon color="white"
                            icon={"play-circle"}
                            size="4x"
                          />
                        </div>

                      </div>

                          <div>
                            <h6 className="cardsmall-col-heading-black">
                              {Moment( new Date(video.created_at)).format("DD MMM,YYYY")}
                            </h6>

                            {/* <h6 className='home-center-series-videos-small-text' >
                                      {video.title}
                                </h6> */}

                            <h6 className='cardsmall-col-sub-heading-gray'>
                              {video.match_desc}
                            </h6>
                          </div>
                    </Link>
                  </div>
                </div>
              </div> 

            </div>
          </div>
        ))}
      </div>
    }

    if (subType === "video") {
      return (<div>
        {contents.data.map((content, id) => (
          <div key={content.id} className="card mb-1">
            <div className="card-body p-3">

              <Link to=''>
                <div className='video-card-image-size-large'>
                  <img src={content.thumb}
                    alt="no Iamge" />

                  <div className='play-button'>
                    <FontAwesomeIcon color="white"
                      icon={"play-circle"}
                      size="6x"
                    />
                  </div>

                </div>

                <h6 className='cardsmall-col-heading-black'>
                  {contents.title}
                </h6>

              </Link>
            </div>
          </div>
        ))}
      </div>
      );
    }

    if (subType === "news") {
      return <div>
        {contents.data.map(news => (
          <div key={news.id} className="card mb-1">
            <div className="card-body p-3">

              <div className='video-card-image-size-large'>
                
                <img src={news.image}
                     alt="no Iamge"
                />
                <div className='play-button'>
                  <FontAwesomeIcon color="white"
                    icon={"play-circle"}
                    size="6x"
                  />
                </div>
              </div>

              <div className="cardsmall-col-heading-black">
                {news.title}
              </div>

            </div>
          </div>
        ))}
      </div>;
    }

    //======================= End Articles =====================================
    if (subType === "article") {
      return <div>
        {contents.data.map(news => (
          <div key={news.id} className="card mb-1">
            <div className="card-body p-3">

                  <div className='video-card-image-size-large'>
                        <img src={news.image}
                             alt="no Iamge"
                        />

                        <div className='play-button'>
                          <FontAwesomeIcon color="white"
                            icon={"play-circle"}
                            size="6x"
                          />
                        </div>
                   </div>

                  <div className="cardsmall-col-heading-black">
                    {news.title}
                  </div>

                  {/* <h6 style={{ fontWeight: "normal" }}>
                        {Moment(new Date(news.created_at)).format("DD MMM, YYYY")}
                      </h6> */}
                </div>
              </div>
        ))}
      </div>;
    }
    //------------------------------- End Articles ----------------------------
  }
}

export default OtherContentsIdentifire;
