import React, { Component } from 'react';
import '../../assets/css/home/homeLeftPanel.css';

class HomeLeftPanel extends Component {

    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            featuredSeries: []
        }

    }


    componentDidMount() {
        fetch("http://54.245.34.7:8080/api/v2/series/featured")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        featuredSeries: result
                    });
                },

                // Handling Erro
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }

    render() {
        const { error, isLoaded, featuredSeries } = this.state;
        
        if (!isLoaded) {
            return (<div> Loading Data ....... Please wait......  </div>)
        } else if (error) {
            return (<div> error : {error.message} </div>)
        } else {
            return (
                <div>
                    <div className="card card-block">
                        <h6 className="card-title p-1">Featured Series</h6>
                        <div className='card-body m-0'>
                            <ul className="list-group list-group-flush">
                                {featuredSeries.featured_series.map(series => (
                                    <li key={series.id} className="list-group-item "> {series.title} </li>
                                ))}
                            </ul>
                        </div>
                    </div>

                    <div className="card card-block mt-1">
                        <ul className="list-group list-group-flush">
                            <li className="list-group-item">Australia v India Test Series 2018/19</li>
                            <li className="list-group-item">Pakistan v New Zealand Test Series in UAE 2018"</li>
                            <li className="list-group-item">pak vs sri test series 2018</li>
                            <li className="list-group-item">Australia v Pakistan Test Series 2018/19</li>
                        </ul>
                    </div>
                </div>
            );
        }
    }
}

export default HomeLeftPanel;
