import React, { Component } from 'react';
//import http from '../../services/httpService';
import request from 'superagent';
import CardImage from '../common/cardImage';
import '../../assets/css/videos/videos.css';

class AllVideos extends Component {
    constructor(props) {
        super(props)
        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            seriesData: [],
            selectedType: 'All_Videos',
            baseUrl: 'https://back.cricwick.net/api/v1',
            page: 1,
            perPage: 5,
            telco: 'telco=mobilink'
        };

    }

    componentDidMount = async () => {
        await this.loadSeriesRecords();
    }

    loadSeriesRecords = async () => {
        const { page, perPage, baseUrl, telco } = this.state;
        //https://back.cricwick.net/api/v1/user_api/get_new_series?page=1&per_page=5&telco=mobilink

        let Url = '';
        // 1) All Videos :
        //    Base URL =  https://back.cricwick.net/api/v1
        //    Composed =  /user_api/get_new_series?page=1&per_page=5&telco=mobilink

        Url = baseUrl + "/user_api"
            + "/"
            + "get_new_series"
            + "?"
            + "page="
            + page
            + "&"
            + "per_page=" + perPage
            + "&" + telco
      
        // try {
        //     const { data } = await http.get(Url);
        //     this.setState({
        //         seriesData : data 
        //     })
        //     console.log(this.state.seriesData);
        // } catch (ex) {
        //     alert("Bad Request......", ex);
        // }

         await request
            .get(Url)
            .set('Accept', 'application/json')
            .then(res => {
                this.setState({
                    isLoaded: true,
                    isLoading: false,
                    seriesData: res.body,
                    hasMore: (res.body.length < perPage)
                });

            })
            .catch(error => {
                this.setState({
                    isLoaded: false,
                    error
                });
            });
    }

    render() {
        const { error, isLoaded, seriesData } = this.state;
        // if (error) {
        //     return (<div> Error : {error.message}</div>);
        // } else if (!isLoaded) {
        //     return (<div> Loading Data.... </div>);
        // } else {
        //------------------------------------------------------------------- 
        return (
            <div className='card p-2'>
                    {seriesData.series.map((series, i) => (
                        <div className='card-columns' key={series.id}>
                            {series.videos.map((video, i) => (
                                <div key={i}>
                                    <CardImage image={video.video.thumb} title={video.video.match_obj.title} detail={video.video.title}/>
                                </div>
                            ))}
                        </div>
                    ))}
            </div>
        )
    }
}
//}

export default AllVideos;
