import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import LoadingSidepanel from '../common/loadingSidepanel';
import '../../assets/css/home/homeLeftPanel.css';

class HomeLeftDownPanel extends Component {

    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            updates: []
        }

    }


    componentDidMount() {
        fetch("http://back.cricwick.net/api/news")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState(() => {
                        return {
                            isLoaded: true,
                            updates: result
                        }
                    });
                },

                // Handling Erro
                (error) => {
                    this.setState(() => {
                        return {
                            isLoaded: true,
                            error
                        }
                    });
                }
            )
    }

    render() {
        const { error, isLoaded, updates } = this.state;

        if (!isLoaded) {
            return (<LoadingSidepanel />)
        } else if (error) {
            return (<div> error : {error.message} </div>)
        } else {
            return (
                <div>
                    <div className="card card-block p-2">
                        <h6 className="card-header">Latest Updates</h6>
                        <div className='card-body p-0 pb-1'>
                            <ul className='list-group pt-1'>
                                {updates.data.map((update, i) => (
                                    <li key={i} className='list-group-item' key={update.id}>
                                        <Link  to={`/stories/${'news'}/${update.id}`} >
                                            {update.title}
                                        </Link>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    </div>
                </div>
            );
        }
    }
}

export default HomeLeftDownPanel;


