import React, { Component } from 'react';
import ReactPlayer from 'react-player';

import Loading   from '../../components/common/loading';
import http      from '../../services/httpService';
import config    from '../../config.json';

import {toast}   from 'react-toastify';
import 'react-toastify/dist/ReactToastify.min.css';

class VideoPlayer extends Component {

    constructor(props) {
        super(props);
        const { id } = this.props;
        
        console.log(props);

        this.state = {
            error: null,
            isLoaded: false,
            isLoading: false,
            hasMore: true,
            video: [],
            id: (id ? id : 0),
            baseUrl: config.apiEndPoint,
            telco: config.telco,
        }
    }

    componentDidMount = async () => {
       //window.addEventListener('scroll', this.handleScroll);
        await this.loadSeriesRecords();
    }

    componentWillUnmount() {
        //window.removeEventListener('scroll', this.handleScroll);
    }

    loadSeriesRecords = async () => {
        const {  baseUrl, telco } = this.state;
        const { id } = this.props;

        // 1) All Videos :
        //    Base URL =  https://back.cricwick.net/api
        //    Composed =  /v1/user_api/get_single_video?id=2457&telco=mobilink

        let Url = baseUrl
            + '/v1'
            + '/user_api'
            + '/get_single_video'
            + '?'
            + 'id=' + id
            + '&'
            + 'telco=' + telco 

        console.log(Url);

        try {
            const { data } = await http.get(Url);
            console.log(data);
            this.setState(() => {
                return {
                    isLoaded: true,
                    isLoading: false,
                    video: data.data,
                }
            });
        } catch (ex) {
            if (ex.response && ex.response.status === 404) {
                toast.error("Errror : ", ex.response.message);
            }
            this.setState({
                isLoaded: false,
                error: ex,
            });
        }
    }

    handleScroll = async () => {
        const { hasMore, page } = this.state;

        let offset = (document.documentElement.scrollTop + window.innerHeight);
        let height = document.documentElement.offsetHeight;

        // console.log(height, "  = ", Match.ceil(offset));

        if (Math.ceil(offset) === height) {
            console.log("Inside Scroll");
            if (!hasMore) {
                //console.log("offset : ", Math.ceil(offset));
                await this.setState((prevState) => { return { page: prevState.page + 1 } });
                await this.loadSeriesRecords();
                console.log("HasMore : ", hasMore, " Page:", page);
            }
            else {
                console.log('End of list....');
            }
        }
    }

    render() {
        const { isLoaded, error, video } = this.state;
        const { showControlls, width, height } =  this.props;

        

        if (error) {
            return (<div> Error : {error.message}</div>);
        } else if (!isLoaded) {
            return (<Loading />);
        } else {
            console.log("Video Data : ", video);
            return (
                <div className="card p-2" id="video-player">
                    {/* <ReactPlayer url={fileUrl} controls={showControlls} width={width} height={height} />
                    <h6 className='cardsmall-col-heading-black mb-1'>{title}</h6>
                    <h6 className='cardsmall-col-sub-heading-gray'>{matchDesc}</h6> */}
                    <h6>Loading Videos</h6>
                </div>
            );
        }
    }
}

export default VideoPlayer;
