import React, { Component } from 'react';
import '../../assets/css/home/homeCenterPanel.css';

class Match extends Component {
    constructor(props) {
        super(props)
        this.state = {
            match: '',
            matchType: '',
        }
    }

    render() {
        const { match, matchType } = this.props;

        if (matchType === 'matches') {
            return (
                <div className='card card-block'>
                    {match.title}

                   <img className='flag-small-image' 
                        src={match.teamA.flag_url}
                        alt="no image"/>
                        {match.teamA.short_name} <br/>
                               


                    <img className='flag-small-image'
                        style={{ width: 20 }}
                        src={match.teamB.flag_url}
                        alt="no image" />
                    <span>
                        {match.teamB.short_name}
                    </span>
                </div>
            )
        }

        if (matchType === 'news') {
            return (
                    <ul>
                        <li>{match.title}</li>
                    </ul>
            )
        }

        if (matchType === 'videos') {
            return (
                <div>videos</div>
            )
        }

        if (matchType === 'articles') {
            return (
                <div>Articles</div>
            )
        }

        return (
            <div>
                       Other than Match
                        News
            </div>
        )
    }
}
