import React, { Component } from 'react';
import Moment from 'moment';
import RenderMatch from './renderMatch';
import {Link} from 'react-router-dom';
import { faHome } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import '../../assets/css/home/homeCenterPanel.css';
import '../../assets/css/common/videoCards.css';

class SeriesContentsIdentifire extends Component {
    constructor(props) {
        super(props)
        this.state = {
            match: '',
            matchType: '',
        }
    }

    //----------------------------------------------------------- 
    // This Method Print Series Title on the first card as header. 
    printMatchCardHeader(printSeriesTitle, seriesTitle) {
        if (printSeriesTitle === 0) {
            return <span className="card-title">
                       {seriesTitle}
                       <hr />
                    </span>
        }
        return;
    }

    //-------------------------------------------------------------------------------
    // 1) This Method Print Particular Series video's Footer the first card as header.
    // 2) Further this function will also print the first big and remaining card 
    //    in text and image side by side format.     
    printVideosCardHeader(printSeriesTitle, match) {

        if (printSeriesTitle === 0) {

            return (
                <div className='card mb-1'>
                    <div className='card-body p-3'>

                        <div>
                            <img className='video-card-image-size-large' style={{ width: 100 + "%" }}
                                 src={match.med_image} alt="no Iamge" />
                             <div>
                                 <FontAwesomeIcon icon={faHome} size='lg'/>
                             </div>     
                        </div>

                        <div>
                            <span className="video-card-title-large">
                                 {match.title}
                             </span>
                        </div>
                    </div>
                </div>
            )

        }
        else {

            return (
                <div className="flex-d">
                    <div className="card mb-1 p-0">
                        <div className='card-body p-3'>
                            <div>
                                <img className='video-card-leftside-size-small'
                                    src={match.med_image}
                                    alt="no Iamge" />
                            </div>

                            <div className='video-card-righttside-size-small' >
                                 <span className= "video-card-righttside-title-small">
                                    {match.title} 
                                </span> <br/>
                                <span className='video-card-righttside-sub-title-small'>
                                     {match.match_desc}
                                </span>
                            </div>
                        </div>
                    </div>
                </div>
            )

        }
    }

    render() {
        const { match, contentType, printSeriesTitle, seriesTitle } = this.props;
        if (contentType === 'matches') {
            return (
              <div>   
                    {match.match_state !== 'Scheduled' &&
                        <Link to={{ pathname: '/summary/', state: { match: match } }}>
                            <div className="card card-block mb-1" style={{ minHeight: 75 + "px" }}>
                                <div className="card-body p-3">
                                    {this.printMatchCardHeader(printSeriesTitle, seriesTitle)}
                                    <RenderMatch match={match} matchType={contentType} />
                                </div>
                            </div>
                        </Link>
                    }

                    {match.match_state === 'Scheduled' &&
                        <div className="card card-block mb-1" style={{ minHeight: 75 + "px" }}>
                            <div className="card-body p-3">
                                {this.printMatchCardHeader(printSeriesTitle, seriesTitle)}
                                <RenderMatch match={match} matchType={contentType} />
                            </div>
                        </div>
                    }
             </div>  

            );
        }

        if (contentType === 'news') {
            return (
                <div>
                    <ul>
                        <li className='news-list'  key={match.id}>
                            <Link to={{
                                pathname: '/stories',
                                state: {
                                    selectedNews: match,
                                }
                            }} >
                                {match.title}
                            </Link>
                        </li>
                    </ul>
                </div>
            )
        }

        if (contentType === 'videos') {
            return (
                <div>
                    {/* Following function will render videos */}
                    {this.printVideosCardHeader(printSeriesTitle, match)}
                </div>
            )
        }

        if (contentType === 'article') {
            return (
                <div className="flex-d">
                    <div className="card mb-1 p-0">
                        <div className='card-body p-3'>
                            <div>
                                <img className='video-card-imageright-size-small'
                                    src={match.image}
                                    alt="no Iamge" />
                            </div>

                            <div className='video-card-textleft-size-small' >
                                
                                <span className='video-card-righttside-title-small'>
                                    {match.title}
                                </span> <br/>
 
                                <span className='video-card-righttside-sub-title-small'>
                                    {Moment(new Date(match.created_at)).format("DD MMM, YYYY")}
                                </span>
                            </div>

                        </div>
                    </div>
                </div>
            )
        }

        if (contentType === 'tweets') {

            return (
                <div className='card mb-1 tweets-card'>
                    <div className='card-body p-3'>
                        <span
                            dangerouslySetInnerHTML={{ __html: match.body }}>
                        </span>
                    </div>
                </div>
            )
        }

        return (
            <div>

            </div>
        )
    }
}

export default SeriesContentsIdentifire;
